import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";import{r as i,o as n,c as d,a,b as s,d as o,e}from"./app.17aaeba5.js";var l="/assets/1.0bf2f969.gif",h="/assets/2.f164e5cf.gif",c="/assets/3.8c8b477f.gif";const p={},g=o('<p>\u901A\u8FC7\u5B8C\u6210\u8BF8\u5982\u521B\u5EFA\u91CD\u505A\u65E5\u5FD7\u7EC4\u548C\u6210\u5458\u3001\u91CD\u65B0\u5B9A\u4F4D\u548C\u91CD\u547D\u540D\u91CD\u505A\u65E5\u5FD7\u6210\u5458\u3001\u5220\u9664\u91CD\u505A\u65E5\u5FD7\u7EC4\u548C\u6210\u5458\u4EE5\u53CA\u5F3A\u5236\u5207\u6362\u65E5\u5FD7\u7B49\u4EFB\u52A1\uFF0C\u60A8\u53EF\u4EE5\u7BA1\u7406\u91CD\u505A\u65E5\u5FD7\u3002</p><h2 id="what-is-the-redo-log" tabindex="-1"><a class="header-anchor" href="#what-is-the-redo-log" aria-hidden="true">#</a> What Is the Redo Log?</h2><p>\u6062\u590D\u64CD\u4F5C\u6700\u5173\u952E\u7684\u7ED3\u6784\u662F\u91CD\u505A\u65E5\u5FD7\uFF0C\u5B83\u7531\u4E24\u4E2A\u6216\u591A\u4E2A\u9884\u5148\u5206\u914D\u7684\u6587\u4EF6\u7EC4\u6210\uFF0C\u8FD9\u4E9B\u6587\u4EF6\u5B58\u50A8\u6570\u636E\u5E93\u4E2D\u53D1\u751F\u7684\u6240\u6709\u66F4\u6539\u3002Oracle\u6570\u636E\u5E93\u7684\u6BCF\u4E2A\u5B9E\u4F8B\u90FD\u6709\u4E00\u4E2A\u76F8\u5173\u7684\u91CD\u505A\u65E5\u5FD7\uFF0C\u4EE5\u4FBF\u5728\u5B9E\u4F8B\u5931\u8D25\u65F6\u4FDD\u62A4\u6570\u636E\u5E93\u3002</p><h3 id="redo-threads" tabindex="-1"><a class="header-anchor" href="#redo-threads" aria-hidden="true">#</a> Redo Threads</h3><p>\u5728\u591A\u4E2A\u6570\u636E\u5E93\u5B9E\u4F8B\u7684\u4E0A\u4E0B\u6587\u4E2D\uFF0C\u6BCF\u4E2A\u6570\u636E\u5E93\u5B9E\u4F8B\u7684\u91CD\u505A\u65E5\u5FD7\u4E5F\u79F0\u4E3A\u91CD\u505A\u7EBF\u7A0B\u3002</p><p>\u5728\u5178\u578B\u7684\u914D\u7F6E\u4E2D\uFF0C\u53EA\u6709\u4E00\u4E2A\u6570\u636E\u5E93\u5B9E\u4F8B\u8BBF\u95EEOracle\u6570\u636E\u5E93\uFF0C\u56E0\u6B64\u53EA\u6709\u4E00\u4E2A\u7EBF\u7A0B\u3002\u7136\u800C\uFF0C\u5728Oracle Real Application Clusters\u73AF\u5883\u4E2D\uFF0C\u4E24\u4E2A\u6216\u591A\u4E2A\u5B9E\u4F8B\u540C\u65F6\u8BBF\u95EE\u5355\u4E2A\u6570\u636E\u5E93\uFF0C\u6BCF\u4E2A\u5B9E\u4F8B\u90FD\u6709\u81EA\u5DF1\u7684\u91CD\u505A\u7EBF\u7A0B\u3002\u6BCF\u4E2A\u5B9E\u4F8B\u7684\u5355\u72EC\u91CD\u505A\u7EBF\u7A0B\u907F\u514D\u4E86\u5BF9\u4E00\u7EC4\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u4E89\u7528\uFF0C\u4ECE\u800C\u6D88\u9664\u4E86\u6F5C\u5728\u7684\u6027\u80FD\u74F6\u9888\u3002</p><p>\u672C\u7AE0\u63CF\u8FF0\u5982\u4F55\u5728\u4E00\u4E2A\u6807\u51C6\u7684\u5355\u5B9E\u4F8BOracle\u6570\u636E\u5E93\u4E0A\u914D\u7F6E\u548C\u7BA1\u7406\u91CD\u505A\u65E5\u5FD7\u3002\u5728\u6240\u6709\u7684\u8BA8\u8BBA\u548C\u8BED\u53E5\u793A\u4F8B\u4E2D\uFF0C\u7EBF\u7A0B\u6570\u53EF\u4EE5\u5047\u5B9A\u4E3A1\u3002\u6709\u5173Oracle Real\u5E94\u7528\u7A0B\u5E8F\u96C6\u7FA4\u73AF\u5883\u4E2D\u7684\u91CD\u505A\u65E5\u5FD7\u7EC4\u7684\u4FE1\u606F\uFF0C\u8BF7\u53C2\u9605Oracle Real\u5E94\u7528\u7A0B\u5E8F\u96C6\u7FA4\u7BA1\u7406\u548C\u90E8\u7F72\u6307\u5357\u3002</p><h3 id="redo-log-contents" tabindex="-1"><a class="header-anchor" href="#redo-log-contents" aria-hidden="true">#</a> Redo Log Contents</h3><p>\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7528\u91CD\u505A\u8BB0\u5F55\u586B\u5145\u3002</p><p>\u91CD\u505A\u8BB0\u5F55(\u4E5F\u79F0\u4E3A\u91CD\u505A\u6761\u76EE)\u7531\u4E00\u7EC4\u66F4\u6539\u5411\u91CF\u7EC4\u6210\uFF0C\u6BCF\u4E2A\u66F4\u6539\u5411\u91CF\u662F\u5BF9\u6570\u636E\u5E93\u4E2D\u5355\u4E2A\u5757\u7684\u66F4\u6539\u7684\u63CF\u8FF0\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u66F4\u6539employee\u8868\u4E2D\u7684salary\u503C\uFF0C\u5219\u4F1A\u751F\u6210\u4E00\u4E2Aredo\u8BB0\u5F55\uFF0C\u5176\u4E2D\u5305\u542B\u63CF\u8FF0\u5BF9\u8868\u7684\u6570\u636E\u6BB5\u5757\u3001undo\u6BB5\u6570\u636E\u5757\u548Cundo\u6BB5\u4E8B\u52A1\u8868\u7684\u66F4\u6539\u5411\u91CF\u3002</p><p>\u91CD\u505A\u6761\u76EE\u8BB0\u5F55\u6570\u636E\uFF0C\u60A8\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E9B\u6570\u636E\u6765\u91CD\u6784\u5BF9\u6570\u636E\u5E93\u6240\u505A\u7684\u6240\u6709\u66F4\u6539\uFF0C\u5305\u62EC\u64A4\u6D88\u6BB5\u3002\u56E0\u6B64\uFF0C\u91CD\u505A\u65E5\u5FD7\u4E5F\u4FDD\u62A4\u56DE\u6EDA\u6570\u636E\u3002\u5F53\u4F7F\u7528\u91CD\u505A\u6570\u636E\u6062\u590D\u6570\u636E\u5E93\u65F6\uFF0C\u6570\u636E\u5E93\u5C06\u8BFB\u53D6\u91CD\u505A\u8BB0\u5F55\u4E2D\u7684\u66F4\u6539\u5411\u91CF\uFF0C\u5E76\u5C06\u66F4\u6539\u5E94\u7528\u4E8E\u76F8\u5173\u5757\u3002</p><p>\u91CD\u505A\u6761\u76EE\u8BB0\u5F55\u6570\u636E\uFF0C\u60A8\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E9B\u6570\u636E\u6765\u91CD\u6784\u5BF9\u6570\u636E\u5E93\u6240\u505A\u7684\u6240\u6709\u66F4\u6539\uFF0C\u5305\u62EC\u64A4\u6D88\u6BB5\u3002\u56E0\u6B64\uFF0C\u91CD\u505A\u65E5\u5FD7\u4E5F\u4FDD\u62A4\u56DE\u6EDA\u6570\u636E\u3002\u5F53\u4F7F\u7528\u91CD\u505A\u6570\u636E\u6062\u590D\u6570\u636E\u5E93\u65F6\uFF0C\u6570\u636E\u5E93\u5C06\u8BFB\u53D6\u91CD\u505A\u8BB0\u5F55\u4E2D\u7684\u66F4\u6539\u5411\u91CF\uFF0C\u5E76\u5C06\u66F4\u6539\u5E94\u7528\u4E8E\u76F8\u5173\u5757\u3002</p><p>\u5728\u63D0\u4EA4\u76F8\u5E94\u7684\u4E8B\u52A1\u4E4B\u524D\uFF0C\u4E5F\u53EF\u4EE5\u5C06\u91CD\u505A\u8BB0\u5F55\u5199\u5165\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002\u5982\u679C\u91CD\u505A\u65E5\u5FD7\u7F13\u51B2\u533A\u88AB\u586B\u6EE1\uFF0C\u6216\u8005\u53E6\u4E00\u4E2A\u4E8B\u52A1\u88AB\u63D0\u4EA4\uFF0CLGWR\u4F1A\u5C06\u91CD\u505A\u65E5\u5FD7\u7F13\u51B2\u533A\u4E2D\u7684\u6240\u6709\u91CD\u505A\u65E5\u5FD7\u9879\u5237\u65B0\u5230\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u4E2D\uFF0C\u5373\u4F7F\u6709\u4E9B\u91CD\u505A\u8BB0\u5F55\u53EF\u80FD\u6CA1\u6709\u88AB\u63D0\u4EA4\u3002\u5982\u679C\u9700\u8981\uFF0C\u6570\u636E\u5E93\u53EF\u4EE5\u56DE\u6EDA\u8FD9\u4E9B\u66F4\u6539\u3002</p><h3 id="how-oracle-database-writes-to-the-redo-log" tabindex="-1"><a class="header-anchor" href="#how-oracle-database-writes-to-the-redo-log" aria-hidden="true">#</a> How Oracle Database Writes to the Redo Log</h3><p>\u6570\u636E\u5E93\u7684\u91CD\u505A\u65E5\u5FD7\u7531\u4E24\u4E2A\u6216\u591A\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7EC4\u6210\u3002\u6570\u636E\u5E93\u81F3\u5C11\u9700\u8981\u4E24\u4E2A\u6587\u4EF6\uFF0C\u4EE5\u786E\u4FDD\u4E00\u4E2A\u6587\u4EF6\u5728\u53E6\u4E00\u4E2A\u6587\u4EF6\u5B58\u6863\u65F6\u59CB\u7EC8\u53EF\u7528(\u5982\u679C\u6570\u636E\u5E93\u5904\u4E8EARCHIVELOG\u6A21\u5F0F)\u3002</p>',15),f=e('See "'),u={href:"https://docs.oracle.com/en/database/oracle/oracle-database/19/admin/managing-archived-redo-log-files.html#GUID-5EE4AC49-E1B2-41A2-BEE7-AA951EAAB2F3",title:"You manage the archived redo log files by completing tasks such as choosing between NOARCHIVELOG\xA0or\xA0ARCHIVELOG\xA0mode and specifying archive destinations.",target:"_blank",rel:"noopener noreferrer"},m=e(" Managing Archived Redo Log Files"),b=e('" for more information.'),L=o('<p>LGWR\u4EE5\u5FAA\u73AF\u65B9\u5F0F\u5199\u5165\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002\u5F53\u5F53\u524D\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u88AB\u586B\u6EE1\u65F6\uFF0CLGWR\u5F00\u59CB\u5411\u4E0B\u4E00\u4E2A\u53EF\u7528\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u5199\u5165\u6570\u636E\u3002\u5F53\u6700\u540E\u4E00\u4E2A\u53EF\u7528\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u88AB\u586B\u6EE1\u65F6\uFF0CLGWR\u8FD4\u56DE\u5230\u7B2C\u4E00\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u5E76\u5BF9\u5176\u8FDB\u884C\u5199\u64CD\u4F5C\uFF0C\u518D\u6B21\u5F00\u59CB\u5FAA\u73AF\u3002\u56FE11-1\u8BF4\u660E\u4E86\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u5FAA\u73AF\u5199\u5165\u3002\u6BCF\u4E00\u884C\u65C1\u8FB9\u7684\u6570\u5B57\u8868\u793ALGWR\u5199\u5165\u6BCF\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u987A\u5E8F\u3002</p><p>\u586B\u5145\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u53EF\u7528\u4E8ELGWR\uFF0C\u4EE5\u6839\u636E\u662F\u5426\u542F\u7528\u5F52\u6863\u8FDB\u884C\u91CD\u7528\u3002</p><ul><li><p>\u5982\u679C\u5F52\u6863\u88AB\u7981\u7528(\u6570\u636E\u5E93\u5904\u4E8ENOARCHIVELOG\u6A21\u5F0F)\uFF0C\u5219\u5728\u5C06\u8BB0\u5F55\u5728\u5176\u4E2D\u7684\u66F4\u6539\u5199\u5165\u6570\u636E\u6587\u4EF6\u4E4B\u540E\uFF0C\u4E00\u4E2A\u5DF2\u586B\u5145\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u662F\u53EF\u7528\u7684\u3002</p></li><li><p>\u5982\u679C\u542F\u7528\u4E86\u5F52\u6863(\u6570\u636E\u5E93\u5904\u4E8EARCHIVELOG\u6A21\u5F0F)\uFF0C\u90A3\u4E48LGWR\u5C06\u8BB0\u5F55\u5728\u5176\u4E2D\u7684\u66F4\u6539\u5199\u5165\u6570\u636E\u6587\u4EF6\u5E76\u5B58\u6863\u4E4B\u540E\uFF0C\u5C31\u53EF\u4EE5\u4F7F\u7528\u586B\u5145\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002</p></li></ul><p><img src="'+l+'" alt="" loading="lazy"></p><h4 id="active-current-and-inactive-redo-log-files" tabindex="-1"><a class="header-anchor" href="#active-current-and-inactive-redo-log-files" aria-hidden="true">#</a> Active (Current) and Inactive Redo Log Files</h4><p>Oracle\u6570\u636E\u5E93\u4E00\u6B21\u53EA\u4F7F\u7528\u4E00\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u6765\u5B58\u50A8\u4ECE\u91CD\u505A\u65E5\u5FD7\u7F13\u51B2\u533A\u5199\u5165\u7684\u91CD\u505A\u8BB0\u5F55\u3002LGWR\u6B63\u5728\u4E3B\u52A8\u5199\u5165\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u79F0\u4E3A\u5F53\u524D\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002</p><p>\u5B9E\u4F8B\u6062\u590D\u6240\u9700\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u79F0\u4E3A\u6D3B\u52A8\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002\u5B9E\u4F8B\u6062\u590D\u4E0D\u518D\u9700\u8981\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u79F0\u4E3A\u975E\u6D3B\u52A8\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002</p><p>\u5982\u679C\u542F\u7528\u4E86\u5F52\u6863(\u6570\u636E\u5E93\u5904\u4E8EARCHIVELOG\u6A21\u5F0F)\uFF0C\u5219\u5728\u5F52\u6863\u540E\u53F0\u8FDB\u7A0B(ARCn)\u4E4B\u4E00\u5F52\u6863\u5176\u5185\u5BB9\u4E4B\u524D\uFF0C\u6570\u636E\u5E93\u4E0D\u80FD\u91CD\u7528\u6216\u8986\u76D6\u6D3B\u52A8\u7684\u5728\u7EBF\u65E5\u5FD7\u6587\u4EF6\u3002\u5982\u679C\u5F52\u6863\u88AB\u7981\u7528(\u6570\u636E\u5E93\u5904\u4E8ENOARCHIVELOG\u6A21\u5F0F)\uFF0C\u90A3\u4E48\u5F53\u6700\u540E\u4E00\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u5DF2\u6EE1\u65F6\uFF0CLGWR\u5C06\u7EE7\u7EED\u8986\u76D6\u4E0B\u4E00\u4E2A\u65E5\u5FD7\u6587\u4EF6\uFF0C\u76F4\u5230\u5B83\u53D8\u5F97\u4E0D\u6D3B\u52A8\u3002</p><h4 id="log-switches-and-log-sequence-numbers" tabindex="-1"><a class="header-anchor" href="#log-switches-and-log-sequence-numbers" aria-hidden="true">#</a> Log Switches and Log Sequence Numbers</h4><p>\u65E5\u5FD7\u5F00\u5173\u662F\u6307\u6570\u636E\u5E93\u505C\u6B62\u5199\u5165\u4E00\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u5E76\u5F00\u59CB\u5199\u5165\u53E6\u4E00\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u70B9\u3002</p><p>\u901A\u5E38\uFF0C\u5F53\u5F53\u524D\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u88AB\u5B8C\u5168\u586B\u6EE1\u5E76\u4E14\u5199\u5165\u5FC5\u987B\u7EE7\u7EED\u5230\u4E0B\u4E00\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u65F6\uFF0C\u4F1A\u53D1\u751F\u65E5\u5FD7\u5207\u6362\u3002</p><p>\u4F46\u662F\uFF0C\u53EF\u4EE5\u5C06\u65E5\u5FD7\u5207\u6362\u914D\u7F6E\u4E3A\u5B9A\u671F\u53D1\u751F\uFF0C\u800C\u4E0D\u7BA1\u5F53\u524D\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u662F\u5426\u5DF2\u5B8C\u5168\u586B\u6EE1\u3002</p><p>\u60A8\u8FD8\u53EF\u4EE5\u624B\u52A8\u5F3A\u5236\u65E5\u5FD7\u5F00\u5173\u3002\u6BCF\u6B21\u53D1\u751F\u65E5\u5FD7\u5207\u6362\u548CLGWR\u5F00\u59CB\u5199\u5165\u65E5\u5FD7\u65F6\uFF0COracle\u6570\u636E\u5E93\u90FD\u4F1A\u4E3A\u6BCF\u4E2A\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u5206\u914D\u4E00\u4E2A\u65B0\u7684\u65E5\u5FD7\u5E8F\u5217\u53F7\u3002\u5F53\u6570\u636E\u5E93\u5F52\u6863\u65F6\u91CD\u505A\u65E5\u5FD7\u8BB0\u5F55</p><h2 id="planning-the-redo-log" tabindex="-1"><a class="header-anchor" href="#planning-the-redo-log" aria-hidden="true">#</a> Planning the Redo Log</h2><h3 id="multiplexing-redo-log-files" tabindex="-1"><a class="header-anchor" href="#multiplexing-redo-log-files" aria-hidden="true">#</a> Multiplexing Redo Log Files</h3><p>\u4E3A\u4E86\u9632\u6B62\u6D89\u53CA\u91CD\u505A\u65E5\u5FD7\u672C\u8EAB\u7684\u6545\u969C\uFF0COracle\u6570\u636E\u5E93\u5141\u8BB8\u4E00\u4E2A\u591A\u8DEF\u91CD\u505A\u65E5\u5FD7\uFF0C\u8FD9\u610F\u5473\u7740\u53EF\u4EE5\u5728\u4E0D\u540C\u7684\u4F4D\u7F6E\u81EA\u52A8\u7EF4\u62A4\u4E24\u4E2A\u6216\u591A\u4E2A\u76F8\u540C\u7684\u91CD\u505A\u65E5\u5FD7\u526F\u672C\u3002</p><p>\u4E3A\u4E86\u83B7\u5F97\u6700\u5927\u7684\u597D\u5904\uFF0C\u8FD9\u4E9B\u4F4D\u7F6E\u5E94\u8BE5\u4F4D\u4E8E\u5355\u72EC\u7684\u78C1\u76D8\u4E0A\u3002\u4F46\u662F\uFF0C\u5373\u4F7F\u91CD\u505A\u65E5\u5FD7\u7684\u6240\u6709\u526F\u672C\u90FD\u5728\u540C\u4E00\u4E2A\u78C1\u76D8\u4E0A\uFF0C\u8FD9\u79CD\u5197\u4F59\u4E5F\u6709\u52A9\u4E8E\u9632\u6B62I/O\u9519\u8BEF\u3001\u6587\u4EF6\u635F\u574F\u7B49\u3002\u5F53\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u88AB\u590D\u7528\u65F6\uFF0CLGWR\u5E76\u53D1\u5730\u5C06\u76F8\u540C\u7684\u91CD\u505A\u65E5\u5FD7\u4FE1\u606F\u5199\u5165\u591A\u4E2A\u76F8\u540C\u7684\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\uFF0C\u4ECE\u800C\u6D88\u9664\u4E86\u91CD\u505A\u65E5\u5FD7\u5931\u8D25\u7684\u5355\u4E00\u70B9\u3002</p><p>\u590D\u7528\u662F\u901A\u8FC7\u521B\u5EFA\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7EC4\u6765\u5B9E\u73B0\u7684\u3002\u7EC4\u7531\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u53CA\u5176\u591A\u8DEF\u590D\u5236\u7EC4\u6210\u3002\u6BCF\u4E2A\u76F8\u540C\u7684\u526F\u672C\u88AB\u79F0\u4E3A\u7EC4\u7684\u4E00\u4E2A\u6210\u5458\u3002\u6BCF\u4E2A\u91CD\u505A\u65E5\u5FD7\u7EC4\u7531\u4E00\u4E2A\u6570\u5B57\u5B9A\u4E49\uFF0C\u6BD4\u5982\u7EC41\u3001\u7EC42\u7B49\u7B49\u3002</p><p><img src="'+h+'" alt="" loading="lazy"></p><p>\u5728\u56FE11-2\u4E2D\uFF0CA_LOG1\u548CB_LOG1\u90FD\u662F\u7EC41\u7684\u6210\u5458\uFF0CA_LOG2\u548CB_LOG2\u90FD\u662F\u7EC42\u7684\u6210\u5458\uFF0C\u4EE5\u6B64\u7C7B\u63A8\u3002\u7EC4\u4E2D\u7684\u6BCF\u4E2A\u6210\u5458\u7684\u5927\u5C0F\u5FC5\u987B\u76F8\u540C\u3002</p><p>\u65E5\u5FD7\u6587\u4EF6\u7EC4\u7684\u6BCF\u4E2A\u6210\u5458\u90FD\u662F\u5E76\u53D1\u6D3B\u52A8\u7684(\u5373lgr\u5E76\u53D1\u5199\u5165\u7684)\uFF0C\u8FD9\u7531LGWR\u5206\u914D\u7684\u76F8\u540C\u65E5\u5FD7\u5E8F\u5217\u53F7\u8868\u793A\u3002\u5728\u56FE11-2\u4E2D\uFF0C\u9996\u5148LGWR\u5E76\u53D1\u5730\u5199A_LOG1\u548CB_LOG1\u3002\u7136\u540E\u5B83\u540C\u65F6\u5199\u5165A_LOG2\u548CB_LOG2\uFF0C\u4EE5\u6B64\u7C7B\u63A8\u3002LGWR\u4ECE\u4E0D\u5E76\u53D1\u5730\u5199\u4E0D\u540C\u7EC4\u7684\u6210\u5458(\u4F8B\u5982\uFF0C\u5199A_LOG1\u548CB_LOG2)\u3002</p><p>\u6CE8\u610F:Oracle\u5EFA\u8BAE\u60A8\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u3002\u5982\u679C\u9700\u8981\u6062\u590D\uFF0C\u65E5\u5FD7\u6587\u4EF6\u6570\u636E\u7684\u4E22\u5931\u53EF\u80FD\u662F\u707E\u96BE\u6027\u7684\u3002\u6CE8\u610F\uFF0C\u5F53\u60A8\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u65F6\uFF0C\u6570\u636E\u5E93\u5FC5\u987B\u589E\u52A0\u5B83\u6267\u884C\u7684I/O\u6570\u91CF\u3002\u6839\u636E\u60A8\u7684\u914D\u7F6E\uFF0C\u8FD9\u53EF\u80FD\u4F1A\u5F71\u54CD\u6574\u4E2A\u6570\u636E\u5E93\u7684\u6027\u80FD\u3002</p><h4 id="responding-to-redo-log-failure" tabindex="-1"><a class="header-anchor" href="#responding-to-redo-log-failure" aria-hidden="true">#</a> Responding to Redo Log Failure</h4><p>\u5F53LGWR\u4E0D\u80FD\u5199\u5165\u7EC4\u7684\u6210\u5458\u65F6\uFF0C\u6570\u636E\u5E93\u5C06\u8BE5\u6210\u5458\u6807\u8BB0\u4E3A\u65E0\u6548\uFF0C\u5E76\u5411LGWR\u8DDF\u8E2A\u6587\u4EF6\u548C\u6570\u636E\u5E93\u8B66\u62A5\u65E5\u5FD7\u5199\u5165\u9519\u8BEF\u6D88\u606F\uFF0C\u4EE5\u6307\u793A\u4E0D\u53EF\u8BBF\u95EE\u6587\u4EF6\u7684\u95EE\u9898\u3002</p><p>\u5F53\u91CD\u505A\u65E5\u5FD7\u6210\u5458\u4E0D\u53EF\u7528\u65F6\uFF0CLGWR\u7684\u5177\u4F53\u53CD\u5E94\u53D6\u51B3\u4E8E\u7F3A\u4E4F\u53EF\u7528\u6027\u7684\u539F\u56E0\uFF0C\u5982\u4E0B\u8868\u6240\u793A\u3002</p><table><thead><tr><th>Condition</th><th>LGWR Action</th></tr></thead><tbody><tr><td>LGWR can successfully write to at least one member in a group</td><td>Writing proceeds as normal. LGWR writes to the available members of a group and ignores the unavailable members.</td></tr><tr><td>LGWR cannot access the next group at a log switch because the group must be archived</td><td>Database operation temporarily halts until the group becomes available or until the group is archived.</td></tr><tr><td>All members of the next group are inaccessible to LGWR at a log switch because of media failure</td><td>Oracle Database returns an error, and the database instance shuts down. In this case, you may need to perform media recovery on the database from the loss of a redo log file.If the database checkpoint has moved beyond the lost redo log, media recovery is not necessary, because the database has saved the data recorded in the redo log to the data files. You need only drop the inaccessible redo log group. If the database did not archive the bad log, use <code>ALTER DATABASE CLEAR LOGFILE UNARCHIVED</code> to disable archiving before the log can be dropped.</td></tr><tr><td>All members of a group suddenly become inaccessible to LGWR while it is writing to them</td><td>Oracle Database returns an error and the database instance immediately shuts down. In this case, you may need to perform media recovery. If the media containing the log is not actually lost--for example, if the drive for the log was inadvertently turned off--media recovery may not be needed. In this case, you need only turn the drive back on and let the database perform automatic instance recovery.</td></tr></tbody></table><h4 id="legal-and-illegal-configurations" tabindex="-1"><a class="header-anchor" href="#legal-and-illegal-configurations" aria-hidden="true">#</a> Legal and Illegal Configurations</h4><p>\u5728\u5927\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u5E94\u8BE5\u662F\u5BF9\u79F0\u7684:\u91CD\u505A\u65E5\u5FD7\u7684\u6240\u6709\u7EC4\u5E94\u8BE5\u5177\u6709\u76F8\u540C\u6570\u91CF\u7684\u6210\u5458\u3002\u4F46\u662F\uFF0C\u6570\u636E\u5E93\u5E76\u4E0D\u8981\u6C42\u591A\u8DEF\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u662F\u5BF9\u79F0\u7684\u3002</p><p>\u4F8B\u5982\uFF0C\u4E00\u4E2A\u7EC4\u53EA\u80FD\u6709\u4E00\u4E2A\u6210\u5458\uFF0C\u800C\u5176\u4ED6\u7EC4\u53EF\u4EE5\u6709\u4E24\u4E2A\u6210\u5458\u3002\u8FD9\u79CD\u914D\u7F6E\u53EF\u4EE5\u9632\u6B62\u78C1\u76D8\u6545\u969C\uFF0C\u56E0\u4E3A\u78C1\u76D8\u6545\u969C\u4F1A\u6682\u65F6\u5F71\u54CD\u67D0\u4E9B\u91CD\u505A\u65E5\u5FD7\u6210\u5458\uFF0C\u4F46\u4E0D\u4F1A\u5F71\u54CD\u5176\u4ED6\u6210\u5458\u3002</p><p>\u5B9E\u4F8B\u91CD\u505A\u65E5\u5FD7\u7684\u60DF\u4E00\u8981\u6C42\u662F\u5B83\u81F3\u5C11\u6709\u4E24\u4E2A\u7EC4\u3002\u56FE11-3\u663E\u793A\u4E86\u5408\u6CD5\u548C\u975E\u6CD5\u7684\u591A\u8DEF\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u914D\u7F6E\u3002\u7B2C\u4E8C\u4E2A\u914D\u7F6E\u662F\u975E\u6CD5\u7684\uFF0C\u56E0\u4E3A\u5B83\u53EA\u6709\u4E00\u4E2A\u7EC4\u3002</p><p><img src="'+c+'" alt="" loading="lazy"></p><h3 id="placing-redo-log-members-on-different-disks" tabindex="-1"><a class="header-anchor" href="#placing-redo-log-members-on-different-disks" aria-hidden="true">#</a> Placing Redo Log Members on Different Disks</h3><p>\u8BBE\u7F6E\u591A\u8DEF\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u65F6\uFF0C\u5C06\u7EC4\u7684\u6210\u5458\u653E\u5728\u4E0D\u540C\u7684\u7269\u7406\u78C1\u76D8\u4E0A\u3002\u5982\u679C\u5355\u4E2A\u78C1\u76D8\u51FA\u73B0\u6545\u969C\uFF0C\u90A3\u4E48LGWR\u53EA\u80FD\u8BBF\u95EE\u7EC4\u4E2D\u7684\u4E00\u4E2A\u6210\u5458\uFF0C\u800CLGWR\u4ECD\u7136\u53EF\u4EE5\u8BBF\u95EE\u5176\u4ED6\u6210\u5458\uFF0C\u56E0\u6B64\u5B9E\u4F8B\u53EF\u4EE5\u7EE7\u7EED\u8FD0\u884C\u3002</p><p>\u5982\u679C\u60A8\u5F52\u6863\u4E86\u91CD\u505A\u65E5\u5FD7\uFF0C\u8BF7\u5C06\u91CD\u505A\u65E5\u5FD7\u6210\u5458\u5206\u6563\u5230\u78C1\u76D8\u4E0A\uFF0C\u4EE5\u6D88\u9664LGWR\u548CARCn\u540E\u53F0\u8FDB\u7A0B\u4E4B\u95F4\u7684\u4E89\u7528\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u60A8\u6709\u4E24\u7EC4\u591A\u8DEF\u590D\u7528\u91CD\u505A\u65E5\u5FD7\u6210\u5458(\u53CC\u8DEF\u590D\u7528\u91CD\u505A\u65E5\u5FD7)\uFF0C\u5219\u5C06\u6BCF\u4E2A\u6210\u5458\u653E\u5728\u4E0D\u540C\u7684\u78C1\u76D8\u4E0A\uFF0C\u5E76\u5C06\u5F52\u6863\u76EE\u6807\u8BBE\u7F6E\u4E3A\u7B2C\u4E94\u4E2A\u78C1\u76D8\u3002\u8FD9\u6837\u505A\u53EF\u4EE5\u907F\u514DLGWR(\u5411\u6210\u5458\u5199\u5165\u6570\u636E)\u548CARCn(\u8BFB\u53D6\u6210\u5458\u6570\u636E)\u4E4B\u95F4\u7684\u4E89\u7528\u3002</p><p>\u8FD8\u5E94\u8BE5\u5C06\u6570\u636E\u6587\u4EF6\u653E\u5728\u4E0E\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u4E0D\u540C\u7684\u78C1\u76D8\u4E0A\uFF0C\u4EE5\u51CF\u5C11\u5199\u5165\u6570\u636E\u5757\u548C\u91CD\u505A\u8BB0\u5F55\u65F6\u7684\u4E89\u7528\u3002</p><h3 id="planning-the-size-of-redo-log-filesplanning-the-size-of-redo-log-files" tabindex="-1"><a class="header-anchor" href="#planning-the-size-of-redo-log-filesplanning-the-size-of-redo-log-files" aria-hidden="true">#</a> Planning the Size of Redo Log FilesPlanning the Size of Redo Log Files</h3><p>\u5728\u8BBE\u7F6E\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u5927\u5C0F\u65F6\uFF0C\u8BF7\u8003\u8651\u662F\u5426\u8981\u5F52\u6863\u91CD\u505A\u65E5\u5FD7\u3002\u5E94\u8BE5\u8C03\u6574\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u5927\u5C0F\uFF0C\u4EE5\u4FBF\u5C06\u586B\u5145\u7684\u7EC4\u5F52\u6863\u5230\u5355\u4E2A\u8131\u673A\u5B58\u50A8\u5A92\u4F53\u5355\u5143(\u5982\u78C1\u5E26\u6216\u78C1\u76D8)\uFF0C\u540C\u65F6\u4F7F\u4ECB\u8D28\u4E0A\u672A\u4F7F\u7528\u7684\u7A7A\u95F4\u6700\u5C11\u3002</p><p>\u4F8B\u5982\uFF0C\u5047\u8BBE\u4E00\u4E2A\u78C1\u5E26\u4E0A\u53EA\u80FD\u5BB9\u7EB3\u4E00\u4E2A\u5DF2\u586B\u5145\u7684\u91CD\u505A\u65E5\u5FD7\u7EC4\uFF0C\u5E76\u4E1449%\u7684\u78C1\u5E26\u5B58\u50A8\u5BB9\u91CF\u4ECD\u7136\u6CA1\u6709\u4F7F\u7528\u3002\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u6700\u597D\u7A0D\u5FAE\u51CF\u5C11\u91CD\u505A\u65E5\u5FD7\u6587\u4EF6\u7684\u5927\u5C0F\uFF0C\u4EE5\u4FBF\u5728\u6BCF\u4E2A\u78C1\u5E26\u4E0A\u5F52\u6863\u4E24\u4E2A\u65E5\u5FD7\u7EC4\u3002</p>',38);function R(_,G){const t=i("ExternalLinkIcon");return n(),d("div",null,[g,a("p",null,[f,a("a",u,[m,s(t)]),b]),L])}var A=r(p,[["render",R],["__file","001-\u7BA1\u7406OracleRedoLog.html.vue"]]);export{A as default};
