import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{d as o}from"./app.22146848.js";var i="/assets/1.fa910f82.png";const l={},c=o('<p>NiFi <code>Connection</code>\u662F\u5728\u4E24\u4E2A\u5DF2\u8FDE\u63A5\u7684NiFi\u5904\u7406\u5668\u7EC4\u4EF6\u4E4B\u95F4\u4E34\u65F6\u4FDD\u5B58FlowFiles\u7684\u4F4D\u7F6E\u3002\u6BCF\u4E2A\u5305\u542B\u6392\u961F\u7684NiFi FlowFiles\u7684<code>Connection</code>\u5728JVM\u5806\u4E2D\u90FD\u4F1A\u5360\u4E00\u4E9B\u7A7A\u95F4\u3002\u672C\u6587\u5C06\u5BF9<code>Connection</code>\u8FDB\u884C\u5206\u6790\uFF0C\u63A2\u7A76NiFi\u5982\u4F55\u7BA1\u7406\u5728\u8BE5<code>Connection</code>\u4E2D\u6392\u961F\u7684FlowFiles\u548C<code>Connection</code>\u5BF9\u5806\u548C\u6027\u80FD\u7684\u5F71\u54CD\u3002</p><h2 id="\u6B63\u6587" tabindex="-1"><a class="header-anchor" href="#\u6B63\u6587" aria-hidden="true">#</a> \u6B63\u6587</h2><p>\u9996\u5148\u770B\u4E00\u4E0B\u4E0B\u9762\u8FD9\u5F20\u8BF4\u660E\u56FE</p><p><img src="'+i+`" alt="" loading="lazy"></p><p>NiFi FlowFiles\u7531FlowFile\u5185\u5BB9\u548CFlowFile\u5C5E\u6027/\u5143\u6570\u636E\u7EC4\u6210\u3002 FlowFile\u5185\u5BB9\u6C38\u8FDC\u4E0D\u4F1A\u4FDD\u5B58\u5728<code>Connection</code>\u4E2D\u3002<code>Connection</code>\u4EC5\u5C06FlowFile\u5C5E\u6027/\u5143\u6570\u636E\u653E\u7F6E\u5728\u5806\u4E2D\u3002</p><h3 id="connection-queue" tabindex="-1"><a class="header-anchor" href="#connection-queue" aria-hidden="true">#</a> &quot;Connection Queue&quot;</h3><p>&quot;Connection Queue&quot;\u4FDD\u5B58\u4E86<code>Connection</code>\u4E2D\u6240\u6709\u6392\u961F\u7684FlowFiles\u7684\u4F4D\u7F6E\u3002\u8981\u4E86\u89E3\u8FD9\u4E9B\u6392\u961F\u7684FlowFile\u5982\u4F55\u5F71\u54CD\u6027\u80FD\u548C\u5806\u4F7F\u7528\u60C5\u51B5\uFF0C\u8BA9\u6211\u4EEC\u9996\u5148\u5173\u6CE8\u4E0A\u56FE\u5E95\u90E8\u7684\u5173\u4E8E&quot;Connection Queue&quot;\u7684\u5256\u6790\u3002</p><p><code>Connection</code>\u7684\u6574\u4F53\u5927\u5C0F\u7531\u7528\u6237\u914D\u7F6E\u7684<code>back Pressure Object Threshold</code>\u548C<code>Back Pressure Data Size threshold</code>\u8BBE\u7F6E\u63A7\u5236\u3002</p><p><strong><code>Back Pressure Object Threshold</code> \u548C <code>Back Pressure Data Size Threshold</code></strong></p><p>\u6B64\u5904\u7684<code>Back Pressure Object Threshold</code>\u9ED8\u8BA4\u8BBE\u7F6E\u4E3A10000\u3002</p><p><code>Back Pressure Data Size Threshold</code>\u9ED8\u8BA4\u4E3A1 GB\u3002</p><p>\u8FD9\u4E24\u4E2A\u8BBE\u7F6E\u90FD\u662F\u8F6F\u9650\u5236\uFF0C\u8FD9\u610F\u5473\u7740\u53EF\u4EE5\u8D85\u8FC7\u5B83\u4EEC\u3002\u4F8B\u5982\uFF0C\u5047\u8BBE\u4E0A\u9762\u7684\u9ED8\u8BA4\u8BBE\u7F6E\u4EE5\u53CA\u5DF2\u7ECF\u5305\u542B9500\u4E2AFlowFiles\u7684\u8FDE\u63A5\u3002\u7531\u4E8E\u8FDE\u63A5\u5C1A\u672A\u8FBE\u5230\u6216\u8D85\u8FC7\u5BF9\u8C61\u9608\u503C\uFF0C\u56E0\u6B64\u5141\u8BB8\u8FD0\u884C\u8BE5\u8FDE\u63A5\u7684\u5904\u7406\u5668\u8FD0\u884C\u3002\u5982\u679C\u6B64\u961F\u5217\u4E0A\u6E38\u7684\u5904\u7406\u5668\u5728\u6267\u884C\u65F6\u53C8\u751F\u6210\u4E862000\u4E2AFlowFiles\uFF0C\u5219<code>Connection</code>\u5C06\u589E\u957F\u523011500\u4E2A\u6392\u961F\u7684FlowFiles\u3002\u7136\u540E\uFF0C\u76F4\u5230<code>Connection</code>\u518D\u6B21\u4E0B\u964D\u5230\u914D\u7F6E\u7684\u9608\u503C\u4EE5\u4E0B\uFF0C\u624D\u5141\u8BB8\u524D\u4E00\u4E2A\u5904\u7406\u5668\u6267\u884C\u3002(\u8FD9\u5C31\u662F\u80CC\u538B\u673A\u5236)</p><p>\u6570\u636E\u5927\u5C0F\u9608\u503C\u4E5F\u662F\u5982\u6B64\u3002\u6570\u636E\u5927\u5C0F\u57FA\u4E8E\u4E0E\u6BCF\u4E2A\u6392\u961F\u7684FlowFile\u76F8\u5173\u8054\u7684\u5185\u5BB9\u7684\u7D2F\u79EF\u5927\u5C0F\u3002</p><p>\u73B0\u5728\uFF0C\u6211\u4EEC\u77E5\u9053\u5982\u4F55\u63A7\u5236\u201Cconnection queue\u201D\u7684\u6574\u4F53\u5927\u5C0F\uFF0C\u4E0B\u9762\u5C06\u5176\u5206\u89E3\u4E3A\u51E0\u4E2A\u90E8\u5206\uFF1A</p><ol><li>ACTIVE QUEUE\uFF1AFlowFiles\u8FDB\u5165\u5230\u4E00\u4E2A<code>Connection</code>\u4E2D\u5C06\u9996\u5148\u88AB\u653E\u7F6E\u5728active\u961F\u5217\u4E2D\u3002 \u4E4B\u540EFlowFiles\u5C06\u7EE7\u7EED\u88AB\u653E\u5165\u5230\u6B64active\u961F\u5217\uFF0C\u76F4\u5230\u8BE5\u961F\u5217\u8FBE\u5230\u5168\u5C40\u914D\u7F6E\u7684nifi\u4EA4\u6362\u9608\u503C\u4E3A\u6B62(swap threshold)\u3002active\u961F\u5217\u4E2D\u7684\u6240\u6709FlowFiles\u90FD\u4FDD\u5B58\u5728\u5806\u5185\u5B58\u4E2D\u3002\u4ECE\u6B64<code>Connection</code>\u4E2D\u4F7F\u7528Flowfile\u7684\u5904\u7406\u5668\u5C06\u59CB\u7EC8\u4ECEactive\u961F\u5217\u4E2D\u63D0\u53D6FlowFiles\u3002\u6BCF\u4E2A\u8FDE\u63A5\u7684\u6D3B\u52A8\u961F\u5217\u7684\u5927\u5C0F\u7531nifi.properties\u6587\u4EF6\u4E2D\u7684\u4EE5\u4E0B\u5C5E\u6027\u63A7\u5236</li></ol><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>nifi.queue.swap.threshold=20000
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u4EA4\u6362\u9608\u503C\u7684\u589E\u52A0\u4F1A\u589E\u52A0\u6570\u636E\u6D41\u4E2D\u6BCF\u4E2A\u8FDE\u63A5\u7684\u6F5C\u5728\u5806\u5360\u7528\u7A7A\u95F4\u3002</p><ol start="2"><li><p>SWAP QUEUE: \u6839\u636E\u4E0A\u8FF0\u9ED8\u8BA4\u8BBE\u7F6E\uFF0C\u4E00\u65E6<code>Connection</code>\u8FBE\u523020000\u4E2AFlowFiles\uFF0C\u8FDB\u5165\u8FDE\u63A5\u7684\u65B0FlowFiles\u5C06\u88AB\u653E\u7F6E\u5728swap\u961F\u5217\u4E2D\u3002swap\u961F\u5217\u4E5F\u4FDD\u5B58\u5728\u5806\u4E2D\uFF0C\u5E76\u4E14\u786C\u7F16\u7801\u4E3A\u6700\u592710000\u4E2AFlowFiles\u3002\u5982\u679C\u6D3B\u52A8\u961F\u5217\u4E2D\u7684\u7A7A\u95F4\u5DF2\u91CA\u653E\u5E76\u4E14\u4E0D\u5B58\u5728\u4EA4\u6362\u6587\u4EF6\uFF0C\u5219\u4EA4\u6362\u961F\u5217\u4E2D\u7684FlowFiles\u5C06\u76F4\u63A5\u79FB\u5230\u6D3B\u52A8\u961F\u5217\u4E2D\u3002</p></li><li><p>SWAP FILES: \u6BCF\u6B21swap\u961F\u5217\u8FBE\u523010000\u4E2AFlowFiles\u65F6\uFF0C\u4F1A\u5C06\u5305\u542B\u8FD9\u4E9BFlowFiles\u7684\u4EA4\u6362\u6587\u4EF6\u5199\u5165\u78C1\u76D8\u4E0A\u3002\u5C4A\u65F6\uFF0C\u65B0\u7684FlowFiles\u5C06\u518D\u6B21\u5199\u5165\u4EA4\u6362\u961F\u5217\u3002NIFI\u53EF\u4EE5\u521B\u5EFA\u8BB8\u591A\u4EA4\u6362\u6587\u4EF6(\u4F46\u8BBE\u8BA1\u4E0A\u5EFA\u8BAE\u5C3D\u91CF\u51CF\u5C11)\uFF0C\u4E0A\u9762\u56FE\u7247\u7684<code>Connection</code>\u5305\u542B80000\u4E2AFlowFiles\uFF0C\u5806\u4E2D\u5C06\u670930000\u4E2AFlowFiles\u548C5\u4E2A\u4EA4\u6362\u6587\u4EF6(active\u4E2D\u6709\u4E24\u4E07\u4E2A\uFF0Cswap\u4E2D\u6709\u4E00\u4E07\u4E2A\uFF0C\u5269\u4E0B\u7684\u4E94\u4E07\u5728\u4EA4\u6362\u6587\u4EF6\u91CC)\u3002\u5F53\u6D3B\u52A8\u961F\u5217\u91CA\u653E10000\u4E2AFlowFiles\uFF0C\u56E0\u6B64\u6700\u65E9\u7684\u4EA4\u6362\u6587\u4EF6\u5C06\u79FB\u81F3\u6D3B\u52A8\u961F\u5217\uFF0C\u76F4\u5230\u6240\u6709\u4EA4\u6362\u6587\u4EF6\u90FD\u6D88\u5931\u3002\u4EA4\u6362\u6587\u4EF6\u4F1A\u4EA7\u751F\u78C1\u76D8IO\u8BFB\u5199\uFF0C\u5728\u6574\u4E2A\u6570\u636E\u6D41\u4E2D\u4EA7\u751F\u5927\u91CF\u4EA4\u6362\u6587\u4EF6\uFF0C\u8FD9\u4E00\u5B9A\u4F1A\u5F71\u54CD\u6570\u636E\u6D41\u7684\u541E\u5410\u91CF\u6027\u80FD\u3002</p></li><li><p>IN-FLIGHT QUEUE: \u4E0E\u4E0A\u9762\u76843\u4E0D\u540C\uFF0C\u8FD0\u884C\u4E2D\u961F\u5217\u4EC5\u5728\u4F7F\u7528\u6B64\u8FDE\u63A5\u7684\u5904\u7406\u5668\u6B63\u5728\u8FD0\u884C\u65F6\u624D\u5B58\u5728\u3002\u6D88\u8D39\u5904\u7406\u5668\u5C06\u4EC5\u4ECEactive\u961F\u5217\u4E2D\u63D0\u53D6FlowFiles\u5E76\u5C06\u5B83\u4EEC\u653E\u7F6E\u5728\u8FD0\u884C\u961F\u5217\u4E2D\uFF0C\u76F4\u5230\u6210\u529F\u5904\u7406\u5B8C\u5E76\u4E14\u8FD9\u4E9BFlowFiles\u5DF2\u4ECE\u6D88\u8D39\u5904\u7406\u5668\u63D0\u4EA4\u5230\u51FA\u7AD9<code>Connection</code>\u4E3A\u6B62\u3002\u8BE5\u8FD0\u884C\u4E2D\u961F\u5217\u4E5F\u4FDD\u7559\u5728\u5806\u4E2D\u3002\u4E00\u4E9B\u5904\u7406\u5668\u4E00\u6B21\u5904\u7406\u4E00\u4E2AFlowFile\uFF0C\u53E6\u4E00\u4E9B\u5904\u7406\u5668\u5904\u7406\u6279\u91CF\u7684FlowFile\uFF0C\u8FD8\u6709\u4E00\u4E9B\u5904\u7406\u5668\u53EF\u80FD\u5904\u7406\u4F20\u5165\u8FDE\u63A5\u961F\u5217\u4E2D\u7684\u6BCF\u4E2AFlowFile\u3002\u5728\u6700\u540E\u4E00\u79CD\u60C5\u51B5\u4E0B\uFF0C\u8FD9\u53EF\u80FD\u610F\u5473\u7740\u5728\u5904\u7406\u8FD9\u4E9BFlowFiles\u65F6\u5806\u4F7F\u7528\u7387\u5F88\u9AD8\u3002\u4E0A\u9762\u7684\u4F7F\u7528MergeContent\u5904\u7406\u5668\u7684\u793A\u4F8B\u5C31\u53EF\u80FD\u662F\u6700\u540E\u4E00\u79CD\u60C5\u51B5\uFF0C\u5047\u5982MergeContent\u914D\u7F6E\u7684\u7ED3\u679C\u4E3A\u6BCF\u6B21\u5408\u5E7690000\u4E2AFlowFile,\u90A3\u4E48\u8FD980000\u4E2AFlowFile\u90FD\u4F1A\u8FDB\u5165\u5230\u8FD0\u884C\u961F\u5217\u4E2D\u3002</p></li></ol><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><ol><li><p>\u901A\u8FC7\u9650\u5236\u8FDE\u63A5\u961F\u5217\u7684\u5927\u5C0F\u6765\u63A7\u5236\u5806\u7684\u4F7F\u7528\uFF08\u5982\u679C\u53EF\u80FD\u7684\u8BDD\uFF09\u3002 \uFF08\u5F53\u7136\uFF0C\u5982\u679C\u4F60\u6253\u7B97\u5408\u5E7640000\u4E2AFlowFile\uFF0C\u5219\u4F20\u5165\u8FDE\u63A5\u4E2D\u5FC5\u987B\u670940,000\u4E2AFlowfile\u3002\u4F46\u662F\uFF0C\u4F60\u53EF\u4EE5\u4E32\u8054\u4F7F\u7528\u4E24\u4E2AmergeContent\u5904\u7406\u5668\uFF0C\u6BCF\u4E2A\u5904\u7406\u5668\u5408\u5E76\u8F83\u5C0F\u7684bundle\uFF0C\u5E76\u83B7\u5F97\u76F8\u540C\u7684\u6700\u7EC8\u7ED3\u679C\uFF0C\u800C\u603B\u5806\u4F7F\u7528\u91CF\u8F83\u5C11\u3002\uFF09</p></li><li><p>\u4F7F\u7528\u9ED8\u8BA4\u7684\u80CC\u538B\u5BF9\u8C61\u9608\u503C\u8BBE\u7F6E\uFF0C\u5927\u591A\u6570\u8FDE\u63A5\u4E0A\u90FD\u4E0D\u4F1A\u751F\u6210\u4EA4\u6362\u6587\u4EF6\uFF08\u8BB0\u4F4F\u8F6F\u9650\u5236\uFF09\uFF0C\u8FD9\u5C06\u5BFC\u81F4\u66F4\u597D\u7684\u541E\u5410\u91CF\u6027\u80FD\u3002</p></li><li><p>\u5728\u5927\u591A\u6570\u6D3B\u52A8\u961F\u5217\u5927\u5C0F\u548C\u6027\u80FD\u7684\u60C5\u51B5\u4E0B\uFF0C\u9ED8\u8BA4\u914D\u7F6E\u7684\u4EA4\u6362\u9608\u503C20000\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u5E73\u8861\u3002\u5BF9\u4E8E\u8F83\u5C0F\u7684\u6D41\u91CF\uFF0C\u4F60\u53EF\u4EE5\u5C06\u5176\u63A8\u9AD8\uFF0C\u5BF9\u4E8E\u8F83\u5927\u7684\u6D41\u91CF\uFF0C\u4F60\u53EF\u80FD\u9700\u8981\u5C06\u5176\u8BBE\u7F6E\u4E3A\u8F83\u4F4E\u3002\u53EA\u9700\u4E86\u89E3\u8FD9\u662F\u4E3A\u4E86\u6027\u80FD\u800C\u5BF9\u5806\u4F7F\u7528\u60C5\u51B5\u8FDB\u884C\u7684\u6743\u8861\u3002\u4F46\u662F\uFF0C\u5982\u679C\u4F60\u7684\u5806\u7528\u5B8C\u4E86\uFF0C\u6027\u80FD\u5C06\u4E3A\u96F6\u3002</p></li></ol><h2 id="\u989D\u5916\u8BF4\u4E00\u4E0B\u961F\u5217\u7684\u4F18\u5148\u7EA7\u6392\u5E8F\u5668" tabindex="-1"><a class="header-anchor" href="#\u989D\u5916\u8BF4\u4E00\u4E0B\u961F\u5217\u7684\u4F18\u5148\u7EA7\u6392\u5E8F\u5668" aria-hidden="true">#</a> \u989D\u5916\u8BF4\u4E00\u4E0B\u961F\u5217\u7684\u4F18\u5148\u7EA7\u6392\u5E8F\u5668</h2><p>\u4F18\u5148\u7EA7\u6392\u5E8F\u5668\u4EC5\u5BF9active\u961F\u5217\u4E2D\u5F53\u524D\u7684FlowFiles\u6709\u6548\u3002\u7531\u4E8E\u6B64active\u961F\u5217\u4F4D\u4E8EJVM\u5806\u4E2D\uFF0C\u56E0\u6B64\u57FA\u4E8E\u4F18\u5148\u7EA7\u7684\u91CD\u65B0\u6392\u5E8F\u5BF9\u6027\u80FD\u7684\u5F71\u54CD\u5F88\u5C0F\u3002\u6BCF\u6B21\u65B0\u7684FlowFile\u8FDB\u5165\u8FDE\u63A5\u65F6\uFF0C\u91CD\u65B0\u8BC4\u4F30\u6240\u6709\u4EA4\u6362\u7684FlowFiles\u90FD\u4F1A\u5F71\u54CD\u541E\u5410\u91CF\u6027\u80FD\u3002\u8BF7\u8BB0\u4F4F\uFF0C\u5F53\u5728\u8FDE\u63A5\u4E0A\u4E0D\u5B9A\u4E49\u4F18\u5148\u7EA7\u65F6\uFF0C\u5C06\u59CB\u7EC8\u83B7\u5F97\u6700\u4F73\u541E\u5410\u91CF\u3002</p>`,22);function n(t,d){return c}var a=e(l,[["render",n],["__file","011-\u7406\u89E3connection.html.vue"]]);export{a as default};
