import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as t,d as n}from"./app.cd174add.js";var r="/assets/1.48dd1e24.png";const i={},o=n('<h2 id="spring-\u6574\u4F53\u67B6\u6784\u56FE" tabindex="-1"><a class="header-anchor" href="#spring-\u6574\u4F53\u67B6\u6784\u56FE" aria-hidden="true">#</a> Spring \u6574\u4F53\u67B6\u6784\u56FE</h2><p>Spring \u6846\u67B6\u662F \u4E2A\u5206\u5C42\u67B6\u6784\uFF0C\u5B83\u5305\u542B\u4E00\u7CFB\u5217\u7684\u529F\u80FD\u8981\u7D20\uFF0C\u5E76\u88AB\u5206\u4E3A\u5927\u7EA6 20 \u6A21\u5757</p><p><img src="'+r+'" alt="" loading="lazy"></p><p>\u8FD9\u4E9B\u6A21\u5757\u88AB\u603B\u7ED3\u4E3A\u4EE5\u4E0B\u51E0\u90E8\u5206</p><h2 id="core-container" tabindex="-1"><a class="header-anchor" href="#core-container" aria-hidden="true">#</a> Core Container</h2><p>Core Container\uFF08\u6838\u5FC3\u5BB9\u5668\uFF09\u5305\u542B\u6709 Core\u3001Beans\u3001Context\u3001Expressio Language \u6A21\u5757\u3002Core\u548CBeans\u6A21\u5757\u662F\u6846\u67B6\u7684\u57FA\u7840\u90E8\u5206\uFF0C\u63D0\u4F9BIoC\uFF08\u8F6C\u63A7\u5236\uFF09\u548C\u4F9D\u8D56\u6CE8\u5165\u7279\u6027\u3002\u8FD9\u91CC\u7684\u57FA\u7840 \u6982\u5FF5\u662FBeanFactory\uFF0C\u5B83\u63D0\u4F9B\u5BF9 Factory \u6A21\u5F0F\u7684\u7ECF\u5178\u5B9E\u73B0\u6765\u6D88\u9664\u5BF9\u7A0B\u5E8F\u6027\u5355\u4F8B\u6A21\u5F0F\u7684\u9700\u8981\uFF0C\u5E76\u771F\u6B63\u5730\u5141\u8BB8\u4F60\u4ECE\u7A0B\u5E8F\u903B\u8F91\u4E2D\u5206\u79BB\u51FA\u4F9D\u8D56\u5173\u7CFB\u548C\u914D\u7F6E</p><ul><li><p>Core\u6A21\u51B3\u4E3B\u8981\u5305\u542BSpring\u6846\u67B6\u57FA\u672C\u7684\u6838\u5FC3\u5DE5\u5177\u7C7B\uFF0CSpring\u7684\u5176\u4ED6\u7EC4\u4EF6\u90FD\u8981\u7528\u5230\u8FD9\u4E2A\u5305\u91CC\u7684\u7C7B\uFF0CCore\u6A21\u5757\u662F\u5176\u4ED6\u7EC4\u4EF6\u7684\u57FA\u672C\u6838\u5FC3\uFF0C\u5F53\u7136\u4F60\u4E5F\u53EF\u4EE5\u5728\u81EA\u5DF1\u7684\u5E94\u7528\u7CFB\u7EDF\u4E2D\u4F7F\u7528\u8FD9\u4E9B\u5DE5\u5177\u7C7B\u3002</p></li><li><p>Beans\u6A21\u5757\u662F\u6240\u6709\u5E94\u7528\u90FD\u8981\u7528\u5230\u7684\uFF0C\u5B83\u5305\u542B\u8BBF\u95EE\u914D\u7F6E\u6587\u4EF6\u3001\u521B\u5EFA\u548C\u7BA1\u7406bean\u4EE5\u53CA\u8FDB\u884CInversion of Control/Dependency Injection (IoC/DI\uFF09\u64CD\u4F5C\u76F8\u5173\u7684\u6240\u6709\u7C7B\u3002</p></li><li><p>Context\u6A21\u5757\u6784\u5EFA\u4E8ECore\u3001Beans\u6A21\u5757\u57FA\u7840\u4E4B\u4E0A\uFF0C\u63D0\u4F9B\u4E86\u4E00\u79CD\u7C7B\u4F3C\u4E8EJNDI\u6CE8\u518C\u5668\u7684\u6846\u67B6\u5F0F\u7684\u5BF9\u8C61\u8BBF\u95EE\u65B9\u6CD5\u3002Context\u6A21\u5757\u7EE7\u627F\u4E86Beans\u7684\u7279\u6027\uFF0C\u4E3ASpring\u6838\u5FC3\u63D0\u4F9B\u4E86\u5927\u91CF\u6269\u5C55\uFF0C\u6DFB\u52A0\u4E86\u5BF9\u56FD\u9645\u5316\uFF08\u4F8B\u5982\u8D44\u6E90\u7ED1\u5B9A\uFF09\u3001\u4E8B\u4EF6\u4F20\u64AD\u3001\u8D44\u6E90\u52A0\u8F7D\u548C\u5BF9Context\u900F\u660E\u521B\u5EFA\u7684\u652F\u6301\u3002Context\u6A21\u5757\u540C\u65F6\u4E5F\u652F\u6301J2EE\u7684\u4E00\u4E9B\u7279\u6027\uFF0C \u4F8B\u5982EJB \u3001JMX\u548C\u57FA\u7840\u7684\u8FDC\u7A0B\u5904\u7406\u3002ApplicationContext\u63A5\u53E3\u662FContext\u6A21\u5757\u7684\u5173\u952E</p></li><li><p>Expression Language\u6A21\u5757\u63D0\u4F9B\u4E86\u5F3A\u5927\u7684\u8868\u8FBE\u5F0F\u8BED\u8A00\uFF0C\u7528\u4E8E\u5728\u8FD0\u884C\u65F6\u67E5\u8BE2\u548C\u64CD\u7EB5\u5BF9\u8C61\u3002\u5B83\u662FJSP2.1\u89C4\u8303\u4E2D\u5B9A\u4E49\u7684<code>unifed expression languag</code>\u7684\u6269\u5C55\u3002\u8BE5\u8BED\u8A00\u652F\u6301\u8BBE\u76F4/\u83B7\u53D6\u5C5E\u6027\u7684\u503C\uFF0C\u5C5E\u6027\u7684\u5206\u914D\uFF0C\u65B9\u6CD5\u7684\u8C03\u7528\uFF0C\u8BBF\u95EE\u6570\u7EC4\u4E0A\u4E0B\u6587\uFF08 accessiong the context of arrays \uFF09\u3001\u5BB9\u5668\u548C\u7D22\u5F15\u5668\u3001\u903B\u8F91\u548C\u7B97\u672F\u8FD0\u7B97\u7B26\u3001\u547D\u540D\u53D8\u91CF\u4EE5\u53CA\u4ECESprit\u5C6F\u7684IoC\u5BB9\u5668\u4E2D\u6839\u636E\u540D\u79F0\u68C0\u7D22\u5BF9\u8C61\u3002\u5B83\u4E5F\u652F\u6301list\u6295\u5F71\u3001\u9009\u62E9\u548C\u4E00\u822C\u7684list\u805A\u5408\u3002</p></li></ul><blockquote><p><strong>\u56FD\u9645\u5316</strong>\uFF08internationalization\uFF09\u662F\u8BBE\u8BA1\u548C\u5236\u9020\u5BB9\u6613\u9002\u5E94\u4E0D\u540C\u533A\u57DF\u8981\u6C42\u7684\u4EA7\u54C1\u7684\u4E00\u79CD\u65B9\u5F0F\u3002 \u5B83\u8981\u6C42\u4ECE\u4EA7\u54C1\u4E2D\u62BD\u79BB\u6240\u6709\u5730\u57DF\u8BED\u8A00\uFF0C\u56FD\u5BB6/\u5730\u533A\u548C\u6587\u5316\u76F8\u5173\u7684\u5143\u7D20\u3002 \u6362\u8A00\u4E4B\uFF0C\u5E94\u7528\u7A0B\u5E8F\u7684\u529F\u80FD\u548C\u4EE3\u7801\u8BBE\u8BA1\u8003\u8651\u5728\u4E0D\u540C\u5730\u533A\u8FD0\u884C\u7684\u9700\u8981\uFF0C\u5176\u4EE3\u7801\u7B80\u5316\u4E86\u4E0D\u540C\u672C\u5730\u7248\u672C\u7684\u751F\u4EA7\u3002 \u5F00\u53D1\u8FD9\u6837\u7684\u7A0B\u5E8F\u7684\u8FC7\u7A0B\uFF0C\u5C31\u79F0\u4E3A\u56FD\u9645\u5316\u3002\u901A\u5E38\u6211\u4EEC\u7528\u5230\u7684\u5C31\u662F\u4EA7\u54C1\u91CC\u7684\u8BED\u8A00\u56FD\u9645\u5316\u3002</p></blockquote><blockquote><p><strong>JNDI</strong>(Java Naming and Directory Interface,Java\u547D\u540D\u548C\u76EE\u5F55\u63A5\u53E3)\u662FSUN\u516C\u53F8\u63D0\u4F9B\u7684\u4E00\u79CD\u6807\u51C6\u7684Java\u547D\u540D\u7CFB\u7EDF\u63A5\u53E3\uFF0CJNDI\u63D0\u4F9B\u7EDF\u4E00\u7684\u5BA2\u6237\u7AEFAPI\uFF0C\u901A\u8FC7\u4E0D\u540C\u7684\u8BBF\u95EE\u63D0\u4F9B\u8005\u63A5\u53E3JNDI\u670D\u52A1\u4F9B\u5E94\u63A5\u53E3(SPI)\u7684\u5B9E\u73B0\uFF0C\u7531\u7BA1\u7406\u8005\u5C06JNDI API\u6620\u5C04\u4E3A\u7279\u5B9A\u7684\u547D\u540D\u670D\u52A1\u548C\u76EE\u5F55\u7CFB\u7EDF\uFF0C\u4F7F\u5F97Java\u5E94\u7528\u7A0B\u5E8F\u53EF\u4EE5\u548C\u8FD9\u4E9B\u547D\u540D\u670D\u52A1\u548C\u76EE\u5F55\u670D\u52A1\u4E4B\u95F4\u8FDB\u884C\u4EA4\u4E92\u3002\u76EE\u5F55\u670D\u52A1\u662F\u547D\u540D\u670D\u52A1\u7684\u4E00\u79CD\u81EA\u7136\u6269\u5C55\u3002\u4E24\u8005\u4E4B\u95F4\u7684\u5173\u952E\u5DEE\u522B\u662F\u76EE\u5F55\u670D\u52A1\u4E2D\u5BF9\u8C61\u4E0D\u4F46\u53EF\u4EE5\u6709\u540D\u79F0\u8FD8\u53EF\u4EE5\u6709\u5C5E\u6027\uFF08\u4F8B\u5982\uFF0C\u7528\u6237\u6709email\u5730\u5740\uFF09\uFF0C\u800C\u547D\u540D\u670D\u52A1\u4E2D\u5BF9\u8C61\u6CA1\u6709\u5C5E\u6027</p></blockquote><blockquote><p><strong>JMS</strong>\u5373Java\u6D88\u606F\u670D\u52A1\uFF08Java Message Service\uFF09\u5E94\u7528\u7A0B\u5E8F\u63A5\u53E3\uFF0C\u662F\u4E00\u4E2AJava\u5E73\u53F0\u4E2D\u5173\u4E8E\u9762\u5411\u6D88\u606F\u4E2D\u95F4\u4EF6\uFF08MOM\uFF09\u7684API\uFF0C\u7528\u4E8E\u5728\u4E24\u4E2A\u5E94\u7528\u7A0B\u5E8F\u4E4B\u95F4\uFF0C\u6216\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u53D1\u9001\u6D88\u606F\uFF0C\u8FDB\u884C\u5F02\u6B65\u901A\u4FE1\u3002Java\u6D88\u606F\u670D\u52A1\u662F\u4E00\u4E2A\u4E0E\u5177\u4F53\u5E73\u53F0\u65E0\u5173\u7684API\uFF0C\u7EDD\u5927\u591A\u6570MOM\u63D0\u4F9B\u5546\u90FD\u5BF9JMS\u63D0\u4F9B\u652F\u6301\u3002</p></blockquote><blockquote><p><strong>\u6295\u5F71</strong>\uFF08Projection\uFF09\u8FD0\u7B97\u662F\u6307\u5C06\u5BF9\u8C61\u8F6C\u6362\u4E3A\u4E00\u79CD\u65B0\u5F62\u5F0F\u7684\u64CD\u4F5C\uFF0C\u8BE5\u5F62\u5F0F\u901A\u5E38\u53EA\u5305\u542B\u90A3\u4E9B\u5C06\u968F\u540E\u4F7F\u7528\u7684\u5C5E\u6027\u3002\u901A\u8FC7\u4F7F\u7528\u6295\u5F71\uFF0C\u60A8\u53EF\u4EE5\u6784\u5EFA\u4F9D\u636E\u6BCF\u4E2A\u5BF9\u8C61\u751F\u6210\u7684\u65B0\u7C7B\u578B\u3002</p></blockquote><h2 id="data-access-integration" tabindex="-1"><a class="header-anchor" href="#data-access-integration" aria-hidden="true">#</a> Data Access/Integration</h2><p>Data Access/Integration\u5C42\u5305\u542BJDBC\u3001ORM\u3001OXM\u3001JMS\u548CTransaction\u6A21\u5757\u3002</p><ul><li><p>JDBC\u6A21\u5757\u63D0\u4F9B\u4E86\u4E00\u4E2AJDBC\u62BD\u8C61\u5C42\uFF0C\u5B83\u53EF\u4EE5\u6D88\u9664\u5197\u957F\u7684JDBC\u7F16\u7801\u548C\u89E3\u6790\u6570\u636E\u5E93\u5382\u5546\u7279\u6709\u7684\u9519\u8BEF\u4EE3\u7801\uFF0C\u8FD9\u4E2A\u6A21\u5757\u5305\u542B\u4E86Spring JDBC\u6570\u636E\u8BBF\u95EE\u8FDB\u884C\u5C01\u88C5\u7684\u6240\u6709\u7C7B\u3002</p></li><li><p>ORM\u6A21\u5757\u4E3A\u6D41\u884C\u7684\u5BF9\u8C61\uFF0D\u5173\u7CFB\u6620\u5C04API\uFF0C\u5982JPA\u3001JDO\u3001Hibernate\u3001iBatis\u7B49\uFF0C\u63D0\u4F9B\u4E86\u4E00\u4E2A\u4EA4\u4E92\u5C42\u3002\u5229\u7528ORM\u5C01\u88C5\u5305\uFF0C\u53EF\u4EE5\u6DF7\u5408\u4F7F\u7528\u6240\u6709Spring\u63D0\u4F9B\u7684\u7279\u6027\u8FDB\u884CO/R\u6620\u5C04\uFF0C\u5982\u524D\u8FB9\u63D0\u5230\u7684\u7B80\u5355\u58F0\u660E\u6027\u4E8B\u52A1\u7BA1\u7406\u3002</p></li><li><p>OXM\u6A21\u5757\u63D0\u4F9B\u4E86\u4E00\u4E2A\u5BF9Object/XML\u6620\u5C04\u5B9E\u73B0\u7684\u62BD\u8C61\u5C42\uFF0CObject/XML\u6620\u5C04\u5B9E\u73B0\u5305\u62ECJAXB\u3001Castor\u3001XMLBeans\u3001JiBX\u3001XStream\u3002</p></li><li><p>JMS ( Java Messaging Service \uFF09\u6A21\u5757\u4E3B\u8981\u5305\u542B\u4E86\u4E00\u4E9B\u5236\u9020\u548C\u6D88\u8D39\u6D88\u606F\u7684\u7279\u6027\u3002</p></li><li><p>Transaction\u6A21\u5757\u652F\u6301\u7F16\u7A0B\u548C\u58F0\u660E\u6027\u7684\u4E8B\u52A1\u7BA1\u7406\uFF0C\u8FD9\u4E9B\u4E8B\u52A1\u7C7B\u5FC5\u987B\u5B9E\u73B0\u7279\u5B9A\u7684\u63A5\u53E3\uFF0C\u5E76\u4E14\u5BF9\u6240\u6709\u7684POJO\u90FD\u9002\u7528</p></li></ul><blockquote><p>\u5BF9\u8C61\u5173\u7CFB\u6620\u5C04\uFF08Object Relational Mapping\uFF0C\u7B80\u79F0ORM\uFF09\u662F\u901A\u8FC7\u4F7F\u7528\u63CF\u8FF0\u5BF9\u8C61\u548C\u6570\u636E\u5E93\u4E4B\u95F4\u6620\u5C04\u7684\u5143\u6570\u636E\uFF0C\u5C06\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u7A0B\u5E8F\u4E2D\u7684\u5BF9\u8C61\u81EA\u52A8\u6301\u4E45\u5316\u5230\u5173\u7CFB\u6570\u636E\u5E93\u4E2D\u3002\u672C\u8D28\u4E0A\u5C31\u662F\u5C06\u6570\u636E\u4ECE\u4E00\u79CD\u5F62\u5F0F\u8F6C\u6362\u5230\u53E6\u5916\u4E00\u79CD\u5F62\u5F0F\u3002 POJO\uFF08Plain Ordinary Java Object\uFF09\u7B80\u5355\u7684Java\u5BF9\u8C61\uFF0C\u5B9E\u9645\u5C31\u662F\u666E\u901AJavaBeans\uFF0C\u662F\u4E3A\u4E86\u907F\u514D\u548CEJB\u6DF7\u6DC6\u6240\u521B\u9020\u7684\u7B80\u79F0\u3002\u4F7F\u7528POJO\u540D\u79F0\u662F\u4E3A\u4E86\u907F\u514D\u548CEJB\u6DF7\u6DC6\u8D77\u6765, \u800C\u4E14\u7B80\u79F0\u6BD4\u8F83\u76F4\u63A5. \u5176\u4E2D\u6709\u4E00\u4E9B\u5C5E\u6027\u53CA\u5176getter setter\u65B9\u6CD5\u7684\u7C7B,\u6CA1\u6709\u4E1A\u52A1\u903B\u8F91\uFF0C\u6709\u65F6\u53EF\u4EE5\u4F5C\u4E3AVO(value -object)\u6216dto(Data Transform Object)\u6765\u4F7F\u7528.\u5F53\u7136,\u5982\u679C\u4F60\u6709\u4E00\u4E2A\u7B80\u5355\u7684\u8FD0\u7B97\u5C5E\u6027\u4E5F\u662F\u53EF\u4EE5\u7684,\u4F46\u4E0D\u5141\u8BB8\u6709\u4E1A\u52A1\u65B9\u6CD5,\u4E5F\u4E0D\u80FD\u643A\u5E26\u6709connection\u4E4B\u7C7B\u7684\u65B9\u6CD5\u3002 POJO \u548CJavaBean\u662F\u6211\u4EEC\u5E38\u89C1\u7684\u4E24\u4E2A\u5173\u952E\u5B57\uFF0C\u4E00\u822C\u5BB9\u6613\u6DF7\u6DC6\uFF0CPOJO\u5168\u79F0\u662FPlain Ordinary Java Object / Pure Old Java Object\uFF0C\u4E2D\u6587\u53EF\u4EE5\u7FFB\u8BD1\u6210\uFF1A\u666E\u901AJava\u7C7B\uFF0C\u5177\u6709\u4E00\u90E8\u5206getter/setter\u65B9\u6CD5\u7684\u90A3\u79CD\u7C7B\u5C31\u53EF\u4EE5\u79F0\u4F5CPOJO\uFF0C\u4F46\u662FJavaBean\u5219\u6BD4 POJO\u590D\u6742\u5F88\u591A\uFF0C Java Bean \u662F\u53EF\u590D\u7528\u7684\u7EC4\u4EF6\uFF0C\u5BF9 Java Bean \u5E76\u6CA1\u6709\u4E25\u683C\u7684\u89C4\u8303\uFF0C\u7406\u8BBA\u4E0A\u8BB2\uFF0C\u4EFB\u4F55\u4E00\u4E2A Java \u7C7B\u90FD\u53EF\u4EE5\u662F\u4E00\u4E2A Bean \u3002\u4F46\u901A\u5E38\u60C5\u51B5\u4E0B\uFF0C\u7531\u4E8E Java Bean \u662F\u88AB\u5BB9\u5668\u6240\u521B\u5EFA\uFF08\u5982 Tomcat) \u7684\uFF0C\u6240\u4EE5 Java Bean \u5E94\u5177\u6709\u4E00\u4E2A\u65E0\u53C2\u7684\u6784\u9020\u5668\uFF0C\u53E6\u5916\uFF0C\u901A\u5E38 Java Bean \u8FD8\u8981\u5B9E\u73B0 Serializable \u63A5\u53E3\u7528\u4E8E\u5B9E\u73B0 Bean \u7684\u6301\u4E45\u6027\u3002 Java Bean \u662F\u4E0D\u80FD\u88AB\u8DE8\u8FDB\u7A0B\u8BBF\u95EE\u7684\u3002JavaBean\u662F\u4E00\u79CD\u7EC4\u4EF6\u6280\u672F\uFF0C\u5C31\u597D\u50CF\u4F60\u505A\u4E86\u4E00\u4E2A\u6273\u5B50\uFF0C\u800C\u8FD9\u4E2A\u6273\u5B50\u4F1A\u5728\u5F88\u591A\u5730\u65B9\u88AB\u62FF\u53BB\u7528\uFF0C\u8FD9\u4E2A\u6273\u5B50\u4E5F\u63D0\u4F9B\u591A\u79CD\u529F\u80FD(\u4F60\u53EF\u4EE5\u62FF\u8FD9\u4E2A\u6273\u5B50\u6273\u3001\u9524\u3001\u64AC\u7B49\u7B49)\uFF0C\u800C\u8FD9\u4E2A\u6273\u5B50\u5C31\u662F\u4E00\u4E2A\u7EC4\u4EF6\u3002\u4E00\u822C\u5728web\u5E94\u7528\u7A0B\u5E8F\u4E2D\u5EFA\u7ACB\u4E00\u4E2A\u6570\u636E\u5E93\u7684\u6620\u5C04\u5BF9\u8C61\u65F6\uFF0C\u6211\u4EEC\u53EA\u80FD\u79F0\u5B83\u4E3APOJO\u3002POJO(Plain Old Java Object)\u8FD9\u4E2A\u540D\u5B57\u7528\u6765\u5F3A\u8C03\u5B83\u662F\u4E00\u4E2A\u666E\u901Ajava\u5BF9\u8C61\uFF0C\u800C\u4E0D\u662F\u4E00\u4E2A\u7279\u6B8A\u7684\u5BF9\u8C61\uFF0C\u5176\u4E3B\u8981\u7528\u6765\u6307\u4EE3\u90A3\u4E9B\u6CA1\u6709\u9075\u4ECE\u7279\u5B9A\u7684Java\u5BF9\u8C61\u6A21\u578B\u3001\u7EA6\u5B9A\u6216\u6846\u67B6\uFF08\u5982EJB\uFF09\u7684Java\u5BF9\u8C61\u3002\u7406\u60F3\u5730\u8BB2\uFF0C\u4E00\u4E2APOJO\u662F\u4E00\u4E2A\u4E0D\u53D7\u4EFB\u4F55\u9650\u5236\u7684Java\u5BF9\u8C61\uFF08\u9664\u4E86Java\u8BED\u8A00\u89C4\u8303\uFF09</p></blockquote><h2 id="web" tabindex="-1"><a class="header-anchor" href="#web" aria-hidden="true">#</a> Web</h2><p>Web\u4E0A\u4E0B\u6587\u6A21\u5757\u5EFA\u7ACB\u5728\u5E94\u7528\u7A0B\u5E8F\u4E0A\u4E0B\u6587\u6A21\u5757\u4E4B\u4E0A\uFF0C\u4E3A\u57FA\u4E8EWeb\u7684\u5E94\u7528\u7A0B\u5E8F\u63D0\u4F9B\u4E86\u4E0A\u4E0B\u6587 \u6240\u4EE5\uFF0CSpring\u6846\u67B6\u652F\u6301\u4E0EJakarta Struts\u7684\u96C6\u6210\u3002Web\u6A21\u5757\u8FD8\u7B80\u5316\u4E86\u5904\u7406\u5927\u90E8\u5206\u8BF7\u6C42\u4EE5\u53CA\u5C06\u8BF7\u6C42\u53C2\u6570\u7ED1\u5B9A \u57DF\u5BF9\u8C61\u7684\u5DE5\u4F5C\u3002Web\u5C42\u5305\u542BWeb\u3001Web-Servlet\u3001Web-Stuts\u548CWeb-Porlet\u6A21\u5757\uFF0C\u5177\u4F53\u8BF4\u660E\u5982\u4E0B</p><ul><li><p>Web\u6A21\u5757\uFF1A\u63D0\u4F9B\u4E86\u57FA\u7840\u7684\u9762\u5411Web\u7684\u96C6\u6210\u7279\u6027\u3002\u4F8B\u5982\uFF0C\u591A\u6587\u4EF6\u4E0A\u4F20\u3001\u4F7F\u7528servlet listeners\u521D\u59CB\u5316IoC\u5BB9\u5668\u4EE5\u53CA\u4E00\u4E2A\u9762\u5411Web\u7684\u5E94\u7528\u4E0A\u4E0B\u6587\uFF0C\u5B83\u8FD8\u5305\u542BSpring\u8FDC\u7A0B\u652F\u6301\u4E2DWeb\u7684\u76F8\u5173\u90E8\u5206\u3002</p></li><li><p>Web-Servlet\u6A21\u5757web.servlet.jar \uFF1A\u8BE5\u6A21\u5757\u5305\u542BSpring model-view-controller ( MVC) \u5B9E\u73B0,Spring MVC\u6846\u67B6\u4F7F\u5F97\u6A21\u578B\u8303\u56F4\u5185\u7684\u4EE3\u7801\u548Cweb forms\u4E4B\u95F4\u80FD\u591F\u6E05\u695A\u5730\u5206\u79BB\u5F00\u6765\uFF0C\u5E76\u4E0ESpring\u6846\u67B6\u7684\u5176\u4ED6\u7279\u6027\u96C6\u6210\u5728\u4E00\u8D77\u3002</p></li><li><p>Web-Struts\u6A21\u5757\uFF1A\u8BE5\u6A21\u5757\u63D0\u4F9B\u4E86\u5BF9Struts\u7684\u652F\u6301\uFF0C\u4F7F\u5F97\u7C7B\u5728Spring\u5E94\u7528\u4E2D\u80FD\u591F\u4E0E\u4E00\u4E2A\u5178\u578B\u7684Struts Web\u5C42\u96C6\u6210\u5728\u4E00\u8D77 \u6CE8\u610F\uFF0C\u8BE5\u652F\u6301\u5728 Spring 3.0 \u4E2D\u5DF2\u88AB\u5F03\u7528\u3002</p></li><li><p>Web-Porlet\u6A21\u5757\uFF1A\u63D0\u4F9B\u4E86\u7528\u4E8EPortlet\u5883\u548CWeb-Servlet\u6A21\u5757\u7684MVC\u7684\u5B9E\u73B0</p></li></ul><h2 id="aop" tabindex="-1"><a class="header-anchor" href="#aop" aria-hidden="true">#</a> AOP</h2><p>AOP\u6A21\u5757\u63D0\u4F9B\u4E86\u4E00\u4E2A\u7B26\u5408AOP\u8054\u76DF\u6807\u51C6\u7684\u9762\u5411\u5207\u9762\u7F16\u7A0B\u7684\u5B9E\u73B0\uFF0C\u5B83\u8BA9\u4F60\u53EF\u4EE5\u5B9A\u4E49\u4F8B\u5982\u65B9\u6CD5\u62E6\u622A\u5668\u548C\u5207\u70B9\uFF0C\u4ECE\u800C\u5C06\u903B\u8F91\u4EE3\u7801\u5206\u5F00\uFF0C\u964D\u4F4E\u5B83\u4EEC\u4E4B\u95F4\u7684\u8C03\u5408\u6027,\u5229\u7528source-level\u7684\u5143\u6570\u636E\u529F\u80FD\uFF0C\u8FD8\u53EF\u4EE5\u5C06\u5404\u79CD\u884C\u4E3A\u4FE1\u606F\u5408\u5E76\u5230\u4F60\u7684\u4EE3\u7801\u4E2D\uFF0C\u8FD9\u6709\u70B9\u50CF.Net\u6280\u672F\u4E2D\u7684attribute\u6982\u5FF5\u3002</p><p>\u901A\u8FC7\u914D\u7F6E\u7BA1\u7406\u7279\u6027\uFF0CSpring AOP\u6A21\u5757\u76F4\u63A5\u5C06\u9762\u5411\u5207\u9762\u7684\u7F16\u7A0B\u529F\u80FD\u96C6\u6210\u5230\u4E86Spring\u6846\u67B6\u4E2D\uFF0C\u6240\u4EE5\u53EF\u4EE5\u5F88\u5BB9\u6613\u5730\u4F7FSpring\u6846\u67B6\u7BA1\u7406\u7684\u4EFB\u4F55\u5BF9\u8C61\u652F\u6301AOP\u3002Spring AOP\u6A21\u5757\u4E3A\u57FA\u4E8ESpring\u7684\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u5BF9\u8C61\u63D0\u4F9B\u4E86\u4E8B\u52A1\u7BA1\u7406\u670D\u52A1\uFF0C\u901A\u8FC7\u4F7F\u7528Spring AOP\uFF0C\u4E0D\u7528\u4F9D\u8D56EJB\u7EC4\u4EF6\uFF0C\u5C31\u53EF\u4EE5\u5C06\u58F0\u660E\u6027\u4E8B\u52A1\u7BA1\u7406\u96C6\u6210\u5230\u5E94\u7528\u7A0B\u5E8F\u4E2D\u3002</p><ul><li><p>Aspects\u6A21\u5757\u63D0\u4F9B\u4E86\u5BF9AspectJ\u7684\u96C6\u6210\u652F\u6301\u3002</p></li><li><p>Instrumentation\u6A21\u5757\u63D0\u4F9B\u4E86class instrumentation\u652F\u6301\u548Cclassloader\u5B9E\u73B0\uFF0C\u4F7F\u5F97\u53EF\u4EE5\u5728\u7279\u5B9A\u7684\u5E94\u7528\u670D\u52A1\u5668\u4E0A\u4F7F\u7528\u3002</p></li></ul><h2 id="test" tabindex="-1"><a class="header-anchor" href="#test" aria-hidden="true">#</a> TEST</h2><p>Test\u6A21\u5757\u652F\u6301\u4F7F\u7528JUnit\u3001TestNG\u5BF9Spring\u7EC4\u4EF6\u8FDB\u884C\u6D4B\u8BD5\u3002</p>',24),p=[o];function s(l,c){return a(),t("div",null,p)}var g=e(i,[["render",s],["__file","1Spring\u7684\u6574\u4F53\u67B6\u6784.html.vue"]]);export{g as default};
