import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{r,o as c,c as l,a as e,b as d,d as o,e as i}from"./app.d2f5a032.js";const n={},h=o("<p>\u5173\u952E\u8BCD\uFF1A <code>ACID</code> <code>UNDO_LOG</code> <code>Buffer Pool \u7F13\u51B2\u533A</code> <code>REDO_LOG</code> <code>MVCC</code> <code>\u810F\u8BFB\u548C\u4E0D\u53EF\u91CD\u590D\u8BFB</code> <code>\u884C\u9501\u8868\u9501</code> <code>\u5E7B\u8BFB</code></p>",1),t={href:"https://zhuanlan.zhihu.com/p/117452178",target:"_blank",rel:"noopener noreferrer"},s=i("https://zhuanlan.zhihu.com/p/117452178"),C=o('<h2 id="\u7B54" tabindex="-1"><a class="header-anchor" href="#\u7B54" aria-hidden="true">#</a> \u7B54</h2><p>Mysql \u91CC\u9762\u7684\u4E8B\u52A1\uFF0C\u6EE1\u8DB3 <code>ACID</code> \u7279\u6027\uFF0C\u6240\u4EE5\u5728\u6211\u770B\u6765\uFF0CMysql \u7684\u4E8B\u52A1\u5B9E\u73B0\u539F\u7406\uFF0C \u5C31\u662F InnoDB \u662F\u5982\u4F55\u4FDD\u8BC1 ACID \u7279\u6027\u7684\u3002</p><p>\u9996\u5148\uFF0CA \u8868\u793A Atomic \u539F\u5B50\u6027\uFF0C\u4E5F\u5C31\u662F\u9700\u8981\u4FDD\u8BC1\u591A\u4E2A DML \u64CD\u4F5C\u662F\u539F\u5B50\u7684\uFF0C\u8981\u4E48\u90FD \u6210\u529F\uFF0C\u8981\u4E48\u90FD\u5931\u8D25\u3002 \u90A3\u4E48\uFF0C\u5931\u8D25\u5C31\u610F\u5473\u7740\u8981\u5BF9\u539F\u672C\u6267\u884C\u6210\u529F\u7684\u6570\u636E\u8FDB\u884C\u56DE\u6EDA\uFF0C\u6240\u4EE5 InnoDB \u8BBE\u8BA1\u4E86\u4E00 \u4E2A UNDO_LOG \u8868\uFF0C\u5728\u4E8B\u52A1\u6267\u884C\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u628A\u4FEE\u6539\u4E4B\u524D\u7684\u6570\u636E\u5FEB\u7167\u4FDD\u5B58\u5230 <code>UNDO_LOG</code> \u91CC\u9762\uFF0C\u4E00\u65E6\u51FA\u73B0\u9519\u8BEF\uFF0C\u5C31\u76F4\u63A5\u4ECE UNDO_LOG \u91CC\u9762\u8BFB\u53D6\u6570\u636E\u6267\u884C\u53CD \u5411\u64CD\u4F5C\u5C31\u884C\u4E86\u3002</p><p>\u5176\u6B21\uFF0CC \u8868\u793A\u4E00\u81F4\u6027\uFF0C\u8868\u793A\u6570\u636E\u7684\u5B8C\u6574\u6027\u7EA6\u675F\u6CA1\u6709\u88AB\u7834\u574F\uFF0C\u8FD9\u4E2A\u66F4\u591A\u662F\u4F9D\u8D56\u4E8E\u4E1A \u52A1\u5C42\u9762\u7684\u4FDD\u8BC1\uFF0C\u6570\u636E\u5E93\u672C\u8EAB\u4E5F\u63D0\u4F9B\u4E86\u4E00\u4E9B\uFF0C\u6BD4\u5982\u4E3B\u952E\u7684\u552F\u4E00\u4F59\u6570\uFF0C\u5B57\u6BB5\u957F\u5EA6\u548C\u7C7B \u578B\u7684\u4FDD\u8BC1\u7B49\u7B49\u3002</p><p>\u63A5\u7740\uFF0CI \u8868\u793A\u4E8B\u7269\u7684\u9694\u79BB\u6027\uFF0C\u4E5F\u5C31\u662F\u591A\u4E2A\u5E76\u884C\u4E8B\u52A1\u5BF9\u540C\u4E00\u4E2A\u6570\u636E\u8FDB\u884C\u64CD\u4F5C\u7684\u65F6\u5019\uFF0C \u5982\u4F55\u907F\u514D\u591A\u4E2A\u4E8B\u52A1\u7684\u5E72\u6270\u5BFC\u81F4\u6570\u636E\u6DF7\u4E71\u7684\u95EE\u9898\u3002 \u800C InnoDB \u5B9E\u73B0\u4E86 SQL92 \u7684\u6807\u51C6\uFF0C\u63D0\u4F9B\u4E86\u56DB\u79CD\u9694\u79BB\u7EA7\u522B\u7684\u5B9E\u73B0\u3002\u5206\u522B\u662F\uFF1A <code>RU\uFF08\u672A\u63D0\u4EA4\u8BFB\uFF09 RC\uFF08\u5DF2\u63D0\u4EA4\u8BFB\uFF09 RR\uFF08\u53EF\u91CD\u590D\u8BFB\uFF09Serializable\uFF08\u4E32\u884C\u5316\uFF09 </code></p><p>InnoDB \u9ED8\u8BA4\u7684\u9694\u79BB\u7EA7\u522B\u662F RR\uFF08\u53EF\u91CD\u590D\u8BFB\uFF09\uFF0C\u7136\u540E\u4F7F\u7528\u4E86 <code>MVCC</code> \u673A\u5236\u89E3\u51B3\u4E86\u810F\u8BFB\u548C\u4E0D\u53EF\u91CD\u590D\u8BFB\u7684\u95EE\u9898\uFF0C\u7136\u540E\u4F7F\u7528\u4E86<code>\u884C\u9501/\u8868\u9501</code>\u7684\u65B9\u5F0F\u89E3\u51B3\u4E86\u5E7B\u8BFB\u7684\u95EE\u9898\u3002</p><p>\u6700\u540E\u4E00\u4E2A\u662F D\uFF0C\u8868\u793A\u6301\u4E45\u6027\uFF0C\u4E5F\u5C31\u662F\u53EA\u8981\u4E8B\u52A1\u63D0\u4EA4\u6210\u529F\uFF0C\u90A3\u5BF9\u4E8E\u8FD9\u4E2A\u6570\u636E\u7684\u7ED3\u679C \u7684\u5F71\u54CD\u4E00\u5B9A\u662F\u6C38\u4E45\u6027\u7684\u3002 \u4E0D\u80FD\u56E0\u4E3A\u5B95\u673A\u6216\u8005\u5176\u4ED6\u539F\u56E0\u5BFC\u81F4\u6570\u636E\u53D8\u66F4\u5931\u6548\u3002</p><p>\u7406\u8BBA\u4E0A\u6765\u8BF4\uFF0C\u4E8B\u52A1\u63D0\u4EA4\u4E4B\u540E\u76F4\u63A5\u628A\u6570\u636E\u6301\u4E45\u5316\u5230\u78C1\u76D8\u5C31\u884C\u4E86\uFF0C\u4F46\u662F\u56E0\u4E3A\u968F\u673A\u78C1\u76D8 IO \u7684\u6548\u7387\u786E\u5B9E\u5F88\u4F4E\uFF0C\u6240\u4EE5 InnoDB \u8BBE\u8BA1\u4E86 <code>Buffer Pool \u7F13\u51B2\u533A</code>\u6765\u4F18\u5316\uFF0C\u4E5F\u5C31\u662F\u6570 \u636E\u53D1\u751F\u53D8\u66F4\u7684\u65F6\u5019\u5148\u66F4\u65B0\u5185\u5B58\u7F13\u51B2\u533A\uFF0C\u7136\u540E\u5728\u5408\u9002\u7684\u65F6\u673A\u518D\u6301\u4E45\u5316\u5230\u78C1\u76D8\u3002 \u90A3\u5728\u6301\u4E45\u5316\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u5982\u679C\u6570\u636E\u5E93\u5B95\u673A\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u6570\u636E\u4E22\u5931\uFF0C\u4E5F\u5C31\u65E0\u6CD5\u6EE1\u8DB3\u6301 \u4E45\u6027\u4E86\u3002 \u6240\u4EE5 InnoDB \u5F15\u5165\u4E86 Redo_LOG \u6587\u4EF6\uFF0C\u8FD9\u4E2A\u6587\u4EF6\u5B58\u50A8\u4E86\u6570\u636E\u88AB\u4FEE\u6539\u4E4B\u540E\u7684\u503C\uFF0C \u5F53\u6211\u4EEC\u901A\u8FC7\u4E8B\u52A1\u5BF9\u6570\u636E\u8FDB\u884C\u53D8\u66F4\u64CD\u4F5C\u7684\u65F6\u5019\uFF0C\u9664\u4E86\u4FEE\u6539\u5185\u5B58\u7F13\u51B2\u533A\u91CC\u9762\u7684\u6570\u636E\u4EE5 \u5916\uFF0C\u8FD8\u4F1A\u628A\u672C\u6B21\u4FEE\u6539\u7684\u503C\u8FFD\u52A0\u5230 REDO_LOG \u91CC\u9762\u3002 \u5F53\u63D0\u4EA4\u4E8B\u52A1\u7684\u65F6\u5019\uFF0C\u76F4\u63A5\u628A REDO_LOG \u65E5\u5FD7\u5237\u5230\u78C1\u76D8\u4E0A\u6301\u4E45\u5316\uFF0C\u4E00\u65E6\u6570\u636E\u5E93\u51FA \u73B0\u5B95\u673A\uFF0C\u5728 Mysql \u91CD\u542F\u5728\u4EE5\u540E\u53EF\u4EE5\u76F4\u63A5\u7528 REDO_LOG \u91CC\u9762\u4FDD\u5B58\u7684\u91CD\u5199\u65E5\u5FD7\u8BFB \u53D6\u51FA\u6765\uFF0C\u518D\u6267\u884C\u4E00\u904D\u4ECE\u800C\u4FDD\u8BC1\u6301\u4E45\u6027\u3002</p><p>\u56E0\u6B64\uFF0C\u5728\u6211\u770B\u6765\uFF0C\u4E8B\u52A1\u7684\u5B9E\u73B0\u539F\u7406\u7684\u6838\u5FC3\u672C\u8D28\u5C31\u662F\u5982\u4F55\u6EE1\u8DB3 ACID \u7684\uFF0C\u5728 InnDB \u91CC\u9762\u7528\u5230\u4E86 MVCC\u3001\u884C\u9501\u8868\u9501\u3001UNDO_LOG\u3001REDO_LOG \u7B49\u673A\u5236\u6765\u4FDD\u8BC1\u3002 \u4EE5\u4E0A\u5C31\u662F\u6211\u5BF9\u8FD9\u4E2A\u95EE\u9898\u7684\u7406\u89E3\uFF01</p><h2 id="\u9762\u8BD5\u70B9\u8BC4" tabindex="-1"><a class="header-anchor" href="#\u9762\u8BD5\u70B9\u8BC4" aria-hidden="true">#</a> \u9762\u8BD5\u70B9\u8BC4</h2><p>InnDB \u7684\u4E8B\u52A1\u5B9E\u73B0\u539F\u7406\uFF0C\u6709\u5F88\u591A\u53EF\u4EE5\u501F\u9274\u7684\u8BBE\u8BA1\u601D\u60F3\uFF0C \u6BD4\u5982<code>\u4E50\u89C2\u9501</code>\u3001\u5229\u7528\u5185\u5B58\u7F13\u51B2\u533A\u7684\u65B9\u5F0F\uFF0C\u4EE5\u7A7A\u95F4\u6362\u65F6\u95F4\u7684\u601D\u60F3\u4F18\u5316\u78C1\u76D8 IO \u7684\u6027\u80FD \u7B49\u7B49\u8FD9\u4E9B\u601D\u60F3\u8FD8\u633A\u91CD\u8981\u7684\uFF0C\u6BD4\u5982\u5728\u5206\u5E03\u5F0F\u4E8B\u52A1\u6846\u67B6 Seata \u7684 AT \u6A21\u5F0F\u7684\u6570\u636E\u56DE\u6EDA\uFF0C\u5C31 \u501F\u9274\u4E86 InnDB \u91CC\u9762 UNDO_LOG \u7684\u8BBE\u8BA1\u601D\u60F3\u3002</p><h2 id="mvcc\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#mvcc\u57FA\u7840" aria-hidden="true">#</a> MVCC\u57FA\u7840</h2><p>MVCC (MultiVersion Concurrency Control) \u53EB\u505A\u591A\u7248\u672C\u5E76\u53D1\u63A7\u5236\u3002</p><p>InnoDB\u7684 MVCC \uFF0C\u662F\u901A\u8FC7\u5728\u6BCF\u884C\u8BB0\u5F55\u7684\u540E\u9762\u4FDD\u5B58\u4E24\u4E2A\u9690\u85CF\u7684\u5217\u6765\u5B9E\u73B0\u7684\u3002\u8FD9\u4E24\u4E2A\u5217\uFF0C\u4E00\u4E2A\u4FDD\u5B58\u4E86\u884C\u7684\u521B\u5EFA\u65F6\u95F4\uFF0C\u4E00\u4E2A\u4FDD\u5B58\u4E86\u884C\u7684\u8FC7\u671F\u65F6\u95F4\uFF0C\u5F53\u7136\u5B58\u50A8\u7684\u5E76\u4E0D\u662F\u5B9E\u9645\u7684\u65F6\u95F4\u503C\uFF0C\u800C\u662F\u7CFB\u7EDF\u7248\u672C\u53F7\u3002\u4EE5\u4E0A\u7247\u6BB5\u6458\u81EA\u300A\u9AD8\u6027\u80FDMysql\u300B\u8FD9\u672C\u4E66\u5BF9MVCC\u7684\u5B9A\u4E49\u3002<code>\u4ED6\u7684\u4E3B\u8981\u5B9E\u73B0\u601D\u60F3\u662F\u901A\u8FC7\u6570\u636E\u591A\u7248\u672C\u6765\u505A\u5230\u8BFB\u5199\u5206\u79BB\u3002\u4ECE\u800C\u5B9E\u73B0\u4E0D\u52A0\u9501\u8BFB\u8FDB\u800C\u505A\u5230\u8BFB\u5199\u5E76\u884C\u3002</code></p><p>MVCC\u5728mysql\u4E2D\u7684\u5B9E\u73B0\u4F9D\u8D56\u7684\u662Fundo log\u4E0Eread view\uFF1A</p><ul><li>undo log :undo log \u4E2D\u8BB0\u5F55\u67D0\u884C\u6570\u636E\u7684\u591A\u4E2A\u7248\u672C\u7684\u6570\u636E\u3002</li><li>read view :\u7528\u6765\u5224\u65AD\u5F53\u524D\u7248\u672C\u6570\u636E\u7684\u53EF\u89C1\u6027\u3002</li></ul><p>\u57FA\u672C\u7279\u5F81</p><ol><li>\u6BCF\u884C\u6570\u636E\u90FD\u5B58\u5728\u4E00\u4E2A\u7248\u672C\uFF0C\u6BCF\u6B21\u6570\u636E\u66F4\u65B0\u65F6\u90FD\u66F4\u65B0\u8BE5\u7248\u672C\u3002</li><li>\u4FEE\u6539\u65F6Copy\u51FA\u5F53\u524D\u7248\u672C\u968F\u610F\u4FEE\u6539\uFF0C\u5404\u4E2A\u4E8B\u52A1\u4E4B\u95F4\u65E0\u5E72\u6270\u3002</li><li>\u4FDD\u5B58\u65F6\u6BD4\u8F83\u7248\u672C\u53F7\uFF0C\u5982\u679C\u6210\u529F\uFF08commit\uFF09\uFF0C\u5219\u8986\u76D6\u539F\u8BB0\u5F55\uFF1B\u5931\u8D25\u5219\u653E\u5F03copy\uFF08rollback\uFF09\u3002</li></ol><p>\u8865\u5145</p><ol><li>MVCC\u624B\u6BB5\u53EA\u9002\u7528\u4E8EMsyql\u9694\u79BB\u7EA7\u522B\u4E2D\u7684\u8BFB\u5DF2\u63D0\u4EA4\uFF08Read committed\uFF09\u548C\u53EF\u91CD\u590D\u8BFB\uFF08Repeatable Read\uFF09\uFF1B</li><li>Read uncimmitted\u7531\u4E8E\u5B58\u5728\u810F\u8BFB\uFF0C\u5373\u80FD\u8BFB\u5230\u672A\u63D0\u4EA4\u4E8B\u52A1\u7684\u6570\u636E\u884C\uFF0C\u6240\u4EE5\u4E0D\u9002\u7528MVCC\uFF1B\u539F\u56E0\u662FMVCC\u7684\u521B\u5EFA\u7248\u672C\u548C\u5220\u9664\u7248\u672C\u53EA\u8981\u5728\u4E8B\u52A1\u63D0\u4EA4\u540E\u624D\u4F1A\u4EA7\u751F\u3002</li><li>\u4E32\u884C\u5316\u7531\u4E8E\u662F\u4F1A\u5BF9\u6240\u6D89\u53CA\u5230\u7684\u8868\u52A0\u9501\uFF0C\u5E76\u975E\u884C\u9501\uFF0C\u81EA\u7136\u4E5F\u5C31\u4E0D\u5B58\u5728\u884C\u7684\u7248\u672C\u63A7\u5236\u95EE\u9898\uFF1B</li><li>\u901A\u8FC7\u4EE5\u4E0A\u603B\u7ED3\uFF0C\u53EF\u77E5\uFF0CMVCC\u4E3B\u8981\u4F5C\u7528\u4E8E\u4E8B\u52A1\u6027\u7684\uFF0C\u6709\u884C\u9501\u63A7\u5236\u7684\u6570\u636E\u5E93\u6A21\u578B\u3002</li></ol><h2 id="\u4E8B\u52A1\u7684\u5B9E\u73B0" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u52A1\u7684\u5B9E\u73B0" aria-hidden="true">#</a> \u4E8B\u52A1\u7684\u5B9E\u73B0</h2><p>\u524D\u9762\u8BB2\u7684\u91CD\u505A\u65E5\u5FD7\uFF0C\u56DE\u6EDA\u65E5\u5FD7\u4EE5\u53CA\u9501\u6280\u672F\u5C31\u662F\u5B9E\u73B0\u4E8B\u52A1\u7684\u57FA\u7840\u3002</p><ul><li>\u4E8B\u52A1\u7684\u539F\u5B50\u6027\u662F\u901A\u8FC7 undo log \u6765\u5B9E\u73B0\u7684</li><li>\u4E8B\u52A1\u7684\u6301\u4E45\u6027\u6027\u662F\u901A\u8FC7 redo log \u6765\u5B9E\u73B0\u7684</li><li>\u4E8B\u52A1\u7684\u9694\u79BB\u6027\u662F\u901A\u8FC7 (\u8BFB\u5199\u9501+MVCC)\u6765\u5B9E\u73B0\u7684</li><li>\u800C\u4E8B\u52A1\u7684\u7EC8\u6781\u5927 boss \u4E00\u81F4\u6027\u662F\u901A\u8FC7\u539F\u5B50\u6027\uFF0C\u6301\u4E45\u6027\uFF0C\u9694\u79BB\u6027\u6765\u5B9E\u73B0\u7684\u3002</li></ul><p>\u539F\u5B50\u6027\uFF0C\u6301\u4E45\u6027\uFF0C\u9694\u79BB\u6027\u6298\u817E\u534A\u5929\u7684\u76EE\u7684\u4E5F\u662F\u4E3A\u4E86\u4FDD\u969C\u6570\u636E\u7684\u4E00\u81F4\u6027\uFF01</p><p>\u603B\u4E4B\uFF0CACID\u53EA\u662F\u4E2A\u6982\u5FF5\uFF0C\u4E8B\u52A1\u6700\u7EC8\u76EE\u7684\u662F\u8981\u4FDD\u969C\u6570\u636E\u7684\u53EF\u9760\u6027\uFF0C\u4E00\u81F4\u6027\u3002</p><h2 id="\u539F\u5B50\u6027\u7684\u5B9E\u73B0" tabindex="-1"><a class="header-anchor" href="#\u539F\u5B50\u6027\u7684\u5B9E\u73B0" aria-hidden="true">#</a> \u539F\u5B50\u6027\u7684\u5B9E\u73B0</h2><h2 id="\u6301\u4E45\u6027\u7684\u5B9E\u73B0" tabindex="-1"><a class="header-anchor" href="#\u6301\u4E45\u6027\u7684\u5B9E\u73B0" aria-hidden="true">#</a> \u6301\u4E45\u6027\u7684\u5B9E\u73B0</h2><h2 id="\u9694\u79BB\u6027\u5B9E\u73B0" tabindex="-1"><a class="header-anchor" href="#\u9694\u79BB\u6027\u5B9E\u73B0" aria-hidden="true">#</a> \u9694\u79BB\u6027\u5B9E\u73B0</h2><p>\u9694\u79BB\u6027\u662F\u4E8B\u52A1ACID\u7279\u6027\u91CC\u6700\u590D\u6742\u7684\u4E00\u4E2A\u3002\u5728SQL\u6807\u51C6\u91CC\u5B9A\u4E49\u4E86\u56DB\u79CD\u9694\u79BB\u7EA7\u522B\uFF0C\u6BCF\u4E00\u79CD\u7EA7\u522B\u90FD\u89C4\u5B9A\u4E00\u4E2A\u4E8B\u52A1\u4E2D\u7684\u4FEE\u6539\uFF0C\u54EA\u4E9B\u662F\u4E8B\u52A1\u4E4B\u95F4\u53EF\u89C1\u7684\uFF0C\u54EA\u4E9B\u662F\u4E0D\u53EF\u89C1\u7684\u3002</p><p>\u8D8A\u4F4E\u7684\u9694\u79BB\u7EA7\u522B\u53EF\u4EE5\u6267\u884C\u8D8A\u9AD8\u7684\u5E76\u53D1\uFF0C\u4F46\u540C\u65F6\u5B9E\u73B0\u590D\u6742\u5EA6\u4EE5\u53CA\u5F00\u9500\u4E5F\u8D8A\u5927\u3002</p><p>Mysql \u9694\u79BB\u7EA7\u522B\u6709\u4EE5\u4E0B\u56DB\u79CD\uFF08\u7EA7\u522B\u7531\u4F4E\u5230\u9AD8\uFF09\uFF1A</p><ul><li>READ UNCOMMITED (\u672A\u63D0\u4EA4\u8BFB)</li><li>READ COMMITED (\u63D0\u4EA4\u8BFB)</li><li>REPEATABLE READ (\u53EF\u91CD\u590D\u8BFB)</li><li>SERIALIZABLE (\u4E32\u884C\u5316)</li></ul><p>\u53EA\u8981\u5F7B\u5E95\u7406\u89E3\u4E86\u9694\u79BB\u7EA7\u522B\u4EE5\u53CA\u4ED6\u7684\u5B9E\u73B0\u539F\u7406\u5C31\u76F8\u5F53\u4E8E\u7406\u89E3\u4E86ACID\u91CC\u7684\u9694\u79BB\u578B\u3002\u524D\u9762\u8BF4\u8FC7\u539F\u5B50\u6027\uFF0C\u9694\u79BB\u6027\uFF0C\u6301\u4E45\u6027\u7684\u76EE\u7684\u90FD\u662F\u4E3A\u4E86\u8981\u505A\u5230\u4E00\u81F4\u6027\uFF0C\u4F46\u9694\u79BB\u578B\u8DDF\u5176\u4ED6\u4E24\u4E2A\u6709\u6240\u533A\u522B\uFF0C\u539F\u5B50\u6027\u548C\u6301\u4E45\u6027\u662F\u4E3A\u4E86\u8981\u5B9E\u73B0\u6570\u636E\u7684\u53EF\u6027\u4FDD\u969C\u9760\uFF0C\u6BD4\u5982\u8981\u505A\u5230\u5B95\u673A\u540E\u7684\u6062\u590D\uFF0C\u4EE5\u53CA\u9519\u8BEF\u540E\u7684\u56DE\u6EDA\u3002</p><p>\u90A3\u4E48\u9694\u79BB\u6027\u662F\u8981\u505A\u5230\u4EC0\u4E48\u5462\uFF1F<code>\u9694\u79BB\u6027\u662F\u8981\u7BA1\u7406\u591A\u4E2A\u5E76\u53D1\u8BFB\u5199\u8BF7\u6C42\u7684\u8BBF\u95EE\u987A\u5E8F</code>\u3002 \u8FD9\u79CD\u987A\u5E8F\u5305\u62EC\u4E32\u884C\u6216\u8005\u662F\u5E76\u884C\u3002</p><p>\u8BF4\u660E\u4E00\u70B9\uFF0C\u5199\u8BF7\u6C42\u4E0D\u4EC5\u4EC5\u662F\u6307insert\u64CD\u4F5C\uFF0C\u53C8\u5305\u62ECupdate\u64CD\u4F5C\u3002</p><p>\u603B\u4E4B\uFF0C\u4ECE\u9694\u79BB\u6027\u7684\u5B9E\u73B0\u53EF\u4EE5\u770B\u51FA\u8FD9\u662F\u4E00\u573A\u6570\u636E\u7684\u53EF\u9760\u6027\u4E0E\u6027\u80FD\u4E4B\u95F4\u7684\u6743\u8861\u3002</p><p>\u53EF\u9760\u6027\u6027\u9AD8\u7684\uFF0C\u5E76\u53D1\u6027\u80FD\u4F4E(\u6BD4\u5982 Serializable)\uFF1B</p><p>\u53EF\u9760\u6027\u4F4E\u7684\uFF0C\u5E76\u53D1\u6027\u80FD\u9AD8(\u6BD4\u5982 Read Uncommited)\u3002</p><h3 id="read-uncommitted" tabindex="-1"><a class="header-anchor" href="#read-uncommitted" aria-hidden="true">#</a> READ UNCOMMITTED</h3><p>\u5728READ UNCOMMITTED\u9694\u79BB\u7EA7\u522B\u4E0B\uFF0C\u4E8B\u52A1\u4E2D\u7684\u4FEE\u6539\u5373\u4F7F\u8FD8\u6CA1\u63D0\u4EA4\uFF0C\u5BF9\u5176\u4ED6\u4E8B\u52A1\u662F\u53EF\u89C1\u7684\u3002\u4E8B\u52A1\u53EF\u4EE5\u8BFB\u53D6\u672A\u63D0\u4EA4\u7684\u6570\u636E\uFF0C\u9020\u6210\u810F\u8BFB\u3002</p><p>\u56E0\u4E3A\u8BFB\u4E0D\u4F1A\u52A0\u4EFB\u4F55\u9501\uFF0C\u6240\u4EE5\u5199\u64CD\u4F5C\u5728\u8BFB\u7684\u8FC7\u7A0B\u4E2D\u4FEE\u6539\u6570\u636E\uFF0C\u6240\u4EE5\u4F1A\u9020\u6210\u810F\u8BFB\u3002\u597D\u5904\u662F\u53EF\u4EE5\u63D0\u5347\u5E76\u53D1\u5904\u7406\u6027\u80FD\uFF0C\u80FD\u505A\u5230\u8BFB\u5199\u5E76\u884C\u3002</p><p>\u4F18\u70B9\uFF1A\u8BFB\u5199\u5E76\u884C\uFF0C\u6027\u80FD\u9AD8\u3002</p><p>\u7F3A\u70B9\uFF1A\u9020\u6210<code>\u810F\u8BFB</code>\u3002</p><h3 id="read-committed" tabindex="-1"><a class="header-anchor" href="#read-committed" aria-hidden="true">#</a> READ COMMITTED</h3><p>\u4E00\u4E2A\u4E8B\u52A1\u7684\u4FEE\u6539\u5728\u4ED6\u63D0\u4EA4\u4E4B\u524D\u7684\u6240\u6709\u4FEE\u6539\uFF0C\u5BF9\u5176\u4ED6\u4E8B\u52A1\u90FD\u662F\u4E0D\u53EF\u89C1\u7684\u3002\u5176\u4ED6\u4E8B\u52A1\u80FD\u8BFB\u5230\u5DF2\u63D0\u4EA4\u7684\u4FEE\u6539\u53D8\u5316\u3002\u5728\u5F88\u591A\u573A\u666F\u4E0B\u8FD9\u79CD\u903B\u8F91\u662F\u53EF\u4EE5\u63A5\u53D7\u7684\u3002</p><p>InnoDB\u5728 READ COMMITTED\uFF0C\u4F7F\u7528<code>\u6392\u5B83\u9501</code>, <code>\u8BFB\u53D6\u6570\u636E\u4E0D\u52A0\u9501\u800C\u662F\u4F7F\u7528\u4E86MVCC\u673A\u5236</code>\u3002\u6216\u8005\u6362\u53E5\u8BDD\u8BF4\u4ED6\u91C7\u7528\u4E86<code>\u8BFB\u5199\u5206\u79BB\u673A\u5236</code>\u3002</p><p>\u4F46\u662F\u8BE5\u7EA7\u522B\u4F1A\u4EA7\u751F<code>\u4E0D\u53EF\u91CD\u8BFB\u4EE5\u53CA\u5E7B\u8BFB</code>\u95EE\u9898\u3002</p><p>\u4EC0\u4E48\u662F\u4E0D\u53EF\u91CD\u8BFB\uFF1F\u5728\u4E00\u4E2A\u4E8B\u52A1\u5185\u591A\u6B21\u8BFB\u53D6\u7684\u7ED3\u679C\u4E0D\u4E00\u6837\u3002</p><p>\u4E3A\u4EC0\u4E48\u4F1A\u4EA7\u751F\u4E0D\u53EF\u91CD\u590D\u8BFB\uFF1F\u8FD9\u8DDF READ COMMITTED \u7EA7\u522B\u4E0B\u7684MVCC\u673A\u5236\u6709\u5173\u7CFB\uFF0C\u5728\u8BE5\u9694\u79BB\u7EA7\u522B\u4E0B\u6BCF\u6B21 select\u7684\u65F6\u5019\u65B0\u751F\u6210\u4E00\u4E2A\u7248\u672C\u53F7\uFF0C\u6240\u4EE5\u6BCF\u6B21select\u7684\u65F6\u5019\u8BFB\u7684\u4E0D\u662F\u4E00\u4E2A\u526F\u672C\u800C\u662F\u4E0D\u540C\u7684\u526F\u672C\u3002</p><p>\u5728\u6BCF\u6B21select\u4E4B\u95F4\u6709\u5176\u4ED6\u4E8B\u52A1\u66F4\u65B0\u4E86\u6211\u4EEC\u8BFB\u53D6\u7684\u6570\u636E\u5E76\u63D0\u4EA4\u4E86\uFF0C\u90A3\u5C31\u51FA\u73B0\u4E86\u4E0D\u53EF\u91CD\u590D\u8BFB\uFF1A</p><h3 id="repeatable-read-mysql\u9ED8\u8BA4\u9694\u79BB\u7EA7\u522B" tabindex="-1"><a class="header-anchor" href="#repeatable-read-mysql\u9ED8\u8BA4\u9694\u79BB\u7EA7\u522B" aria-hidden="true">#</a> REPEATABLE READ(Mysql\u9ED8\u8BA4\u9694\u79BB\u7EA7\u522B)</h3><p>\u5728\u4E00\u4E2A\u4E8B\u52A1\u5185\u7684\u591A\u6B21\u8BFB\u53D6\u7684\u7ED3\u679C\u662F\u4E00\u6837\u7684\u3002\u8FD9\u79CD\u7EA7\u522B\u4E0B\u53EF\u4EE5\u907F\u514D\uFF0C\u810F\u8BFB\uFF0C\u4E0D\u53EF\u91CD\u590D\u8BFB\u7B49\u67E5\u8BE2\u95EE\u9898\u3002mysql \u6709\u4E24\u79CD\u673A\u5236\u53EF\u4EE5\u8FBE\u5230\u8FD9\u79CD\u9694\u79BB\u7EA7\u522B\u7684\u6548\u679C\uFF0C\u5206\u522B\u662F\u91C7\u7528\u8BFB\u5199\u9501\u4EE5\u53CAMVCC\u3002</p><p>\u91C7\u7528\u8BFB\u5199\u9501\u5B9E\u73B0\uFF1A</p><p>\u4E3A\u4EC0\u4E48\u80FD\u53EF\u91CD\u590D\u5EA6\uFF1F\u53EA\u8981\u6CA1\u91CA\u653E\u8BFB\u9501\uFF0C\u5728\u6B21\u8BFB\u7684\u65F6\u5019\u8FD8\u662F\u53EF\u4EE5\u8BFB\u5230\u7B2C\u4E00\u6B21\u8BFB\u7684\u6570\u636E\u3002</p><p>\u4F18\u70B9\uFF1A\u5B9E\u73B0\u8D77\u6765\u7B80\u5355\u3002</p><p>\u7F3A\u70B9\uFF1A\u65E0\u6CD5\u505A\u5230\u8BFB\u5199\u5E76\u884C\u3002</p><p>\u91C7\u7528MVCC\u5B9E\u73B0\uFF1A</p><p>\u4E3A\u4EC0\u4E48\u80FD\u53EF\u91CD\u590D\u5EA6\uFF1F\u56E0\u4E3A\u591A\u6B21\u8BFB\u53D6\u53EA\u751F\u6210\u4E00\u4E2A\u7248\u672C\uFF0C\u8BFB\u5230\u7684\u81EA\u7136\u662F\u76F8\u540C\u6570\u636E\u3002</p><p>\u4F18\u70B9\uFF1A\u8BFB\u5199\u5E76\u884C \u3002</p><p>\u7F3A\u70B9\uFF1A\u5B9E\u73B0\u7684\u590D\u6742\u5EA6\u9AD8\u3002</p><p>\u4F46\u662F\u5728\u8BE5\u9694\u79BB\u7EA7\u522B\u4E0B\u4ECD\u4F1A\u5B58\u5728\u5E7B\u8BFB\u7684\u95EE\u9898\uFF0C\u5173\u4E8E\u5E7B\u8BFB\u7684\u89E3\u51B3\u6211\u6253\u7B97\u53E6\u5F00\u4E00\u7BC7\u6765\u4ECB\u7ECD\u3002</p><h3 id="serializable" tabindex="-1"><a class="header-anchor" href="#serializable" aria-hidden="true">#</a> SERIALIZABLE</h3><p>\u8BE5\u9694\u79BB\u7EA7\u522B\u7406\u89E3\u8D77\u6765\u6700\u7B80\u5355\uFF0C\u5B9E\u73B0\u4E5F\u6700\u5355\u3002\u5728\u9694\u79BB\u7EA7\u522B\u4E0B\u9664\u4E86\u4E0D\u4F1A\u9020\u6210\u6570\u636E\u4E0D\u4E00\u81F4\u95EE\u9898\uFF0C\u6CA1\u5176\u4ED6\u4F18\u70B9\u3002</p><h2 id="\u5E7B\u8BFB\u88AB\u5B8C\u5168\u89E3\u51B3\u4E86\u5417" tabindex="-1"><a class="header-anchor" href="#\u5E7B\u8BFB\u88AB\u5B8C\u5168\u89E3\u51B3\u4E86\u5417" aria-hidden="true">#</a> \u5E7B\u8BFB\u88AB\u5B8C\u5168\u89E3\u51B3\u4E86\u5417\uFF1F</h2>',64),D={href:"https://zhuanlan.zhihu.com/p/564735312",target:"_blank",rel:"noopener noreferrer"},_=i("https://zhuanlan.zhihu.com/p/564735312");function u(M,m){const a=r("ExternalLinkIcon");return c(),l("div",null,[h,e("p",null,[e("a",t,[s,d(a)])]),C,e("p",null,[e("a",D,[_,d(a)])])])}var O=p(n,[["render",u],["__file","3MySQL\u4E8B\u52A1\u5B9E\u73B0\u539F\u7406.html.vue"]]);export{O as default};
