import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{d as p}from"./app.5c332ac6.js";var i="/assets/1.80205738.png",r="/assets/2.a2c0688c.png",t="/assets/3.d8938084.png",s="/assets/4.b1d35227.png",e="/assets/5.f2deb00a.png",n="/assets/6.b0f658f9.png",o="/assets/7.66218c1f.png",l="/assets/8.e343979e.png",d="/assets/9.c7cb9a6e.png";const c={},h=p('<h2 id="\u5E76\u53D1\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u5E76\u53D1\u63A7\u5236" aria-hidden="true">#</a> \u5E76\u53D1\u63A7\u5236</h2><p>\u5F53\u7A0B\u5E8F\u4E2D\u53EF\u80FD\u51FA\u73B0\u5E76\u53D1\u7684\u60C5\u51B5\u65F6\uFF0C\u6211\u4EEC\u5C31\u9700\u8981\u901A\u8FC7\u4E00\u5B9A\u7684\u624B\u6BB5\u6765\u4FDD\u8BC1\u5728\u5E76\u53D1\u60C5\u51B5\u4E0B\u6570\u636E\u7684\u51C6\u786E\u6027\uFF0C\u901A\u8FC7\u8FD9\u79CD\u624B\u6BB5\u4FDD\u8BC1\u4E86\u5F53\u524D\u7528\u6237\u548C\u5176\u4ED6\u7528\u6237\u4E00\u8D77\u64CD\u4F5C\u65F6\uFF0C\u6240\u5F97\u5230\u7684\u7ED3\u679C\u548C\u4ED6\u5355\u72EC\u64CD\u4F5C\u65F6\u7684\u7ED3\u679C\u662F\u4E00\u6837\u7684\u3002\u8FD9\u79CD\u624B\u6BB5\u5C31\u53EB\u505A\u5E76\u53D1\u63A7\u5236\u3002\u5E76\u53D1\u63A7\u5236\u7684\u76EE\u7684\u662F\u4FDD\u8BC1\u4E00\u4E2A\u7528\u6237\u7684\u5DE5\u4F5C\u4E0D\u4F1A\u5BF9\u53E6\u4E00\u4E2A\u7528\u6237\u7684\u5DE5\u4F5C\u4EA7\u751F\u4E0D\u5408\u7406\u7684\u5F71\u54CD\u3002</p><p><strong>\u6CA1\u6709\u505A\u597D\u5E76\u53D1\u63A7\u5236\uFF0C\u5C31\u53EF\u80FD\u5BFC\u81F4\u810F\u8BFB\u3001\u5E7B\u8BFB\u548C\u4E0D\u53EF\u91CD\u590D\u8BFB\u7B49\u95EE\u9898\u3002\u6CA1\u6709\u505A\u597D\u5E76\u53D1\u63A7\u5236\uFF0C\u5C31\u53EF\u80FD\u5BFC\u81F4\u810F\u8BFB\u3001\u5E7B\u8BFB\u548C\u4E0D\u53EF\u91CD\u590D\u8BFB\u7B49\u95EE\u9898\u3002</strong></p><p><img src="'+i+'" alt="" loading="lazy"></p><p>\u6211\u4EEC\u5E38\u8BF4\u7684\u5E76\u53D1\u63A7\u5236\uFF0C\u4E00\u822C\u90FD\u548C\u6570\u636E\u5E93\u7BA1\u7406\u7CFB\u7EDF\uFF08DBMS\uFF09\u6709\u5173\u3002\u5728DBMS\u4E2D\u7684\u5E76\u53D1\u63A7\u5236\u7684\u4EFB\u52A1\uFF0C\u662F\u786E\u4FDD\u5728\u591A\u4E2A\u4E8B\u52A1\u540C\u65F6\u5B58\u53D6\u6570\u636E\u5E93\u4E2D\u540C\u4E00\u6570\u636E\u65F6\uFF0C\u4E0D\u7834\u574F\u4E8B\u52A1\u7684\u9694\u79BB\u6027\u548C\u7EDF\u4E00\u6027\u4EE5\u53CA\u6570\u636E\u5E93\u7684\u7EDF\u4E00\u6027\u3002</p><h2 id="\u5B9E\u73B0\u5E76\u53D1\u63A7\u5236\u7684\u624B\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u73B0\u5E76\u53D1\u63A7\u5236\u7684\u624B\u6BB5" aria-hidden="true">#</a> \u5B9E\u73B0\u5E76\u53D1\u63A7\u5236\u7684\u624B\u6BB5</h2><p><strong>\u5B9E\u73B0\u5E76\u53D1\u63A7\u5236\u7684\u4E3B\u8981\u624B\u6BB5\u5927\u81F4\u53EF\u4EE5\u5206\u4E3A\u4E50\u89C2\u5E76\u53D1\u63A7\u5236\u548C\u60B2\u89C2\u5E76\u53D1\u63A7\u5236\u4E24\u79CD\u3002</strong><br> \u5728\u5F00\u59CB\u4ECB\u7ECD\u4E4B\u524D\u8981\u660E\u786E\u4E00\u4E0B\uFF1A\u65E0\u8BBA\u662F\u60B2\u89C2\u9501\u8FD8\u662F\u4E50\u89C2\u9501\uFF0C\u90FD\u662F\u4EBA\u4EEC\u5B9A\u4E49\u51FA\u6765\u7684\u6982\u5FF5\uFF0C\u53EF\u4EE5\u8BA4\u4E3A\u662F\u4E00\u79CD\u601D\u60F3\u3002\u5176\u5B9E\u4E0D\u4EC5\u4EC5\u662F\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7CFB\u7EDF\u4E2D\u6709\u4E50\u89C2\u9501\u548C\u60B2\u89C2\u9501\u7684\u6982\u5FF5\uFF0C\u50CFhibernate\u3001tair\u3001memcache\u7B49\u90FD\u6709\u7C7B\u4F3C\u7684\u6982\u5FF5\u3002\u6240\u4EE5\uFF0C\u4E0D\u5E94\u8BE5\u62FF\u4E50\u89C2\u9501\u3001\u60B2\u89C2\u9501\u548C\u5176\u4ED6\u7684\u6570\u636E\u5E93\u9501\u7B49\u8FDB\u884C\u5BF9\u6BD4\u3002</p><h2 id="\u60B2\u89C2\u9501-pessimistic-lock" tabindex="-1"><a class="header-anchor" href="#\u60B2\u89C2\u9501-pessimistic-lock" aria-hidden="true">#</a> \u60B2\u89C2\u9501\uFF08Pessimistic Lock\uFF09</h2><p>\u5F53\u6211\u4EEC\u8981\u5BF9\u4E00\u4E2A\u6570\u636E\u5E93\u4E2D\u7684\u4E00\u6761\u6570\u636E\u8FDB\u884C\u4FEE\u6539\u7684\u65F6\u5019\uFF0C\u4E3A\u4E86\u907F\u514D\u540C\u65F6\u88AB\u5176\u4ED6\u4EBA\u4FEE\u6539\uFF0C\u6700\u597D\u7684\u529E\u6CD5\u5C31\u662F\u76F4\u63A5\u5BF9\u8BE5\u6570\u636E\u8FDB\u884C\u52A0\u9501\u4EE5\u9632\u6B62\u5E76\u53D1\u3002\u8FD9\u79CD\u501F\u52A9\u6570\u636E\u5E93\u9501\u673A\u5236\uFF0C\u5728\u4FEE\u6539\u6570\u636E\u4E4B\u524D\u5148\u9501\u5B9A\uFF0C\u518D\u4FEE\u6539\u7684\u65B9\u5F0F\u88AB\u79F0\u4E4B\u4E3A\u60B2\u89C2\u5E76\u53D1\u63A7\u5236\uFF08\u53C8\u540D\u201C\u60B2\u89C2\u9501\u201D\uFF0CPessimistic Concurrency Control\uFF0C\u7F29\u5199\u201CPCC\u201D\uFF09\u3002</p><p><strong>\u767E\u5EA6\u767E\u79D1\uFF1A</strong></p><p>\u60B2\u89C2\u9501\uFF0C\u6B63\u5982\u5176\u540D\uFF0C\u5177\u6709\u5F3A\u70C8\u7684\u72EC\u5360\u548C\u6392\u4ED6\u7279\u6027\u3002\u5B83\u6307\u7684\u662F\u5BF9\u6570\u636E\u88AB\u5916\u754C\uFF08\u5305\u62EC\u672C\u7CFB\u7EDF\u5F53\u524D\u7684\u5176\u4ED6\u4E8B\u52A1\uFF0C\u4EE5\u53CA\u6765\u81EA\u5916\u90E8\u7CFB\u7EDF\u7684\u4E8B\u52A1\u5904\u7406\uFF09\u4FEE\u6539\u6301\u4FDD\u5B88\u6001\u5EA6\u3002\u56E0\u6B64\uFF0C\u5728\u6574\u4E2A\u6570\u636E\u5904\u7406\u8FC7\u7A0B\u4E2D\uFF0C\u5C06\u6570\u636E\u5904\u4E8E\u9501\u5B9A\u72B6\u6001\u3002\u60B2\u89C2\u9501\u7684\u5B9E\u73B0\uFF0C\u5F80\u5F80\u4F9D\u9760\u6570\u636E\u5E93\u63D0\u4F9B\u7684\u9501\u673A\u5236\uFF08\u4E5F\u53EA\u6709\u6570\u636E\u5E93\u5C42\u63D0\u4F9B\u7684\u9501\u673A\u5236\u624D\u80FD\u771F\u6B63\u4FDD\u8BC1\u6570\u636E\u8BBF\u95EE\u7684\u6392\u4ED6\u6027\uFF0C\u5426\u5219\uFF0C\u5373\u4F7F\u5728\u672C\u7CFB\u7EDF\u4E2D\u5B9E\u73B0\u4E86\u52A0\u9501\u673A\u5236\uFF0C\u4E5F\u65E0\u6CD5\u4FDD\u8BC1\u5916\u90E8\u7CFB\u7EDF\u4E0D\u4F1A\u4FEE\u6539\u6570\u636E\uFF09\u3002</p><p>\u4E4B\u6240\u4EE5\u53EB\u505A\u60B2\u89C2\u9501\uFF0C\u662F\u56E0\u4E3A\u8FD9\u662F\u4E00\u79CD\u5BF9\u6570\u636E\u7684\u4FEE\u6539\u62B1\u6709\u60B2\u89C2\u6001\u5EA6\u7684\u5E76\u53D1\u63A7\u5236\u65B9\u5F0F\u3002\u6211\u4EEC\u4E00\u822C\u8BA4\u4E3A\u6570\u636E\u88AB\u5E76\u53D1\u4FEE\u6539\u7684\u6982\u7387\u6BD4\u8F83\u5927\uFF0C\u6240\u4EE5\u9700\u8981\u5728\u4FEE\u6539\u4E4B\u524D\u5148\u52A0\u9501\u3002</p><p><strong>\u60B2\u89C2\u9501\u4E3B\u8981\u662F\u5171\u4EAB\u9501\u6216\u6392\u4ED6\u9501</strong></p><ul><li>\u5171\u4EAB\u9501\u53C8\u79F0\u4E3A\u8BFB\u9501\uFF0C\u7B80\u79F0S\u9501\u3002\u987E\u540D\u601D\u4E49\uFF0C\u5171\u4EAB\u9501\u5C31\u662F\u591A\u4E2A\u4E8B\u52A1\u5BF9\u4E8E\u540C\u4E00\u6570\u636E\u53EF\u4EE5\u5171\u4EAB\u4E00\u628A\u9501\uFF0C\u90FD\u80FD\u8BBF\u95EE\u5230\u6570\u636E\uFF0C\u4F46\u662F\u53EA\u80FD\u8BFB\u4E0D\u80FD\u4FEE\u6539\u3002</li><li>\u6392\u4ED6\u9501\u53C8\u79F0\u4E3A\u5199\u9501\uFF0C\u7B80\u79F0X\u9501\u3002\u987E\u540D\u601D\u4E49\uFF0C\u6392\u4ED6\u9501\u5C31\u662F\u4E0D\u80FD\u4E0E\u5176\u4ED6\u9501\u5E76\u5B58\uFF0C\u5982\u679C\u4E00\u4E2A\u4E8B\u52A1\u83B7\u53D6\u4E86\u4E00\u4E2A\u6570\u636E\u884C\u7684\u6392\u4ED6\u9501\uFF0C\u5176\u4ED6\u4E8B\u52A1\u5C31\u4E0D\u80FD\u518D\u83B7\u53D6\u8BE5\u884C\u7684\u5176\u4ED6\u9501\uFF0C\u5305\u62EC\u5171\u4EAB\u9501\u548C\u6392\u4ED6\u9501\uFF0C\u4F46\u662F\u83B7\u53D6\u6392\u4ED6\u9501\u7684\u4E8B\u52A1\u662F\u53EF\u4EE5\u5BF9\u6570\u636E\u884C\u8BFB\u53D6\u548C\u4FEE\u6539\u3002</li></ul><p>\u60B2\u89C2\u5E76\u53D1\u63A7\u5236\u5B9E\u9645\u4E0A\u662F\u201C\u5148\u53D6\u9501\u518D\u8BBF\u95EE\u201D\u7684\u4FDD\u5B88\u7B56\u7565\uFF0C\u4E3A\u6570\u636E\u5904\u7406\u7684\u5B89\u5168\u63D0\u4F9B\u4E86\u4FDD\u8BC1\u3002</p><p><img src="'+r+'" alt="" loading="lazy"></p><p>\u4F46\u662F\u5728\u6548\u7387\u65B9\u9762\uFF0C\u5904\u7406\u52A0\u9501\u7684\u673A\u5236\u4F1A\u8BA9\u6570\u636E\u5E93\u4EA7\u751F\u989D\u5916\u7684\u5F00\u9500\uFF0C\u8FD8\u6709\u589E\u52A0\u4EA7\u751F\u6B7B\u9501\u7684\u673A\u4F1A\u3002\u53E6\u5916\u8FD8\u4F1A\u964D\u4F4E\u5E76\u884C\u6027\uFF0C\u4E00\u4E2A\u4E8B\u52A1\u5982\u679C\u9501\u5B9A\u4E86\u67D0\u884C\u6570\u636E\uFF0C\u5176\u4ED6\u4E8B\u52A1\u5C31\u5FC5\u987B\u7B49\u5F85\u8BE5\u4E8B\u52A1\u5904\u7406\u5B8C\u624D\u53EF\u4EE5\u5904\u7406\u90A3\u884C\u6570\u636E\u3002</p><h2 id="\u4E50\u89C2\u9501-optimistic-locking" tabindex="-1"><a class="header-anchor" href="#\u4E50\u89C2\u9501-optimistic-locking" aria-hidden="true">#</a> \u4E50\u89C2\u9501\uFF08 Optimistic Locking \uFF09</h2><p>\u4E50\u89C2\u9501\u662F\u76F8\u5BF9\u60B2\u89C2\u9501\u800C\u8A00\u7684\uFF0C\u4E50\u89C2\u9501\u5047\u8BBE\u6570\u636E\u4E00\u822C\u60C5\u51B5\u4E0B\u4E0D\u4F1A\u9020\u6210\u51B2\u7A81\uFF0C\u6240\u4EE5\u5728\u6570\u636E\u8FDB\u884C\u63D0\u4EA4\u66F4\u65B0\u7684\u65F6\u5019\uFF0C\u624D\u4F1A\u6B63\u5F0F\u5BF9\u6570\u636E\u7684\u51B2\u7A81\u4E0E\u5426\u8FDB\u884C\u68C0\u6D4B\uFF0C\u5982\u679C\u53D1\u73B0\u51B2\u7A81\u4E86\uFF0C\u5219\u8FD4\u56DE\u7ED9\u7528\u6237\u9519\u8BEF\u7684\u4FE1\u606F\uFF0C\u8BA9\u7528\u6237\u51B3\u5B9A\u5982\u4F55\u53BB\u505A\u3002</p><p><strong>\u767E\u5EA6\u767E\u79D1\uFF1A</strong></p><p>\u4E50\u89C2\u9501\u673A\u5236\u91C7\u53D6\u4E86\u66F4\u52A0\u5BBD\u677E\u7684\u52A0\u9501\u673A\u5236\u3002\u4E50\u89C2\u9501\u662F\u76F8\u5BF9\u60B2\u89C2\u9501\u800C\u8A00\uFF0C\u4E5F\u662F\u4E3A\u4E86\u907F\u514D\u6570\u636E\u5E93\u5E7B\u8BFB\u3001\u4E1A\u52A1\u5904\u7406\u65F6\u95F4\u8FC7\u957F\u7B49\u539F\u56E0\u5F15\u8D77\u6570\u636E\u5904\u7406\u9519\u8BEF\u7684\u4E00\u79CD\u673A\u5236\uFF0C\u4F46\u4E50\u89C2\u9501\u4E0D\u4F1A\u523B\u610F\u4F7F\u7528\u6570\u636E\u5E93\u672C\u8EAB\u7684\u9501\u673A\u5236\uFF0C\u800C\u662F\u4F9D\u636E\u6570\u636E\u672C\u8EAB\u6765\u4FDD\u8BC1\u6570\u636E\u7684\u6B63\u786E\u6027\u3002</p><p>\u76F8\u5BF9\u4E8E\u60B2\u89C2\u9501\uFF0C\u5728\u5BF9\u6570\u636E\u5E93\u8FDB\u884C\u5904\u7406\u7684\u65F6\u5019\uFF0C\u4E50\u89C2\u9501\u5E76\u4E0D\u4F1A\u4F7F\u7528\u6570\u636E\u5E93\u63D0\u4F9B\u7684\u9501\u673A\u5236\u3002\u4E00\u822C\u7684\u5B9E\u73B0\u4E50\u89C2\u9501\u7684\u65B9\u5F0F\u5C31\u662F\u8BB0\u5F55\u6570\u636E\u7248\u672C\u3002</p><p><img src="'+t+'" alt="" loading="lazy"></p><p>\u4E50\u89C2\u5E76\u53D1\u63A7\u5236\u76F8\u4FE1\u4E8B\u52A1\u4E4B\u95F4\u7684\u6570\u636E\u7ADE\u4E89(data race)\u7684\u6982\u7387\u662F\u6BD4\u8F83\u5C0F\u7684\uFF0C\u56E0\u6B64\u5C3D\u53EF\u80FD\u76F4\u63A5\u505A\u4E0B\u53BB\uFF0C\u76F4\u5230\u63D0\u4EA4\u7684\u65F6\u5019\u624D\u53BB\u9501\u5B9A\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u4EA7\u751F\u4EFB\u4F55\u9501\u548C\u6B7B\u9501\u3002</p><h2 id="\u5B9E\u73B0\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u73B0\u65B9\u5F0F" aria-hidden="true">#</a> \u5B9E\u73B0\u65B9\u5F0F</h2><h3 id="\u60B2\u89C2\u9501\u5B9E\u73B0\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u60B2\u89C2\u9501\u5B9E\u73B0\u65B9\u5F0F" aria-hidden="true">#</a> \u60B2\u89C2\u9501\u5B9E\u73B0\u65B9\u5F0F</h3><p>\u60B2\u89C2\u9501\u7684\u5B9E\u73B0\uFF0C\u5F80\u5F80\u4F9D\u9760\u6570\u636E\u5E93\u63D0\u4F9B\u7684\u9501\u673A\u5236\u3002\u5728\u6570\u636E\u5E93\u4E2D\uFF0C\u60B2\u89C2\u9501\u7684\u6D41\u7A0B\u5982\u4E0B\uFF1A</p><ol><li>\u5728\u5BF9\u8BB0\u5F55\u8FDB\u884C\u4FEE\u6539\u524D\uFF0C\u5148\u5C1D\u8BD5\u4E3A\u8BE5\u8BB0\u5F55\u52A0\u4E0A\u6392\u4ED6\u9501\uFF08exclusive locking\uFF09\u3002</li><li>\u5982\u679C\u52A0\u9501\u5931\u8D25\uFF0C\u8BF4\u660E\u8BE5\u8BB0\u5F55\u6B63\u5728\u88AB\u4FEE\u6539\uFF0C\u90A3\u4E48\u5F53\u524D\u67E5\u8BE2\u53EF\u80FD\u8981\u7B49\u5F85\u6216\u8005\u629B\u51FA\u5F02\u5E38\u3002\u5177\u4F53\u54CD\u5E94\u65B9\u5F0F\u7531\u5F00\u53D1\u8005\u6839\u636E\u5B9E\u9645\u9700\u8981\u51B3\u5B9A\u3002</li><li>\u5982\u679C\u6210\u529F\u52A0\u9501\uFF0C\u90A3\u4E48\u5C31\u53EF\u4EE5\u5BF9\u8BB0\u5F55\u505A\u4FEE\u6539\uFF0C\u4E8B\u52A1\u5B8C\u6210\u540E\u5C31\u4F1A\u89E3\u9501\u4E86\u3002</li><li>\u671F\u95F4\u5982\u679C\u6709\u5176\u4ED6\u5BF9\u8BE5\u8BB0\u5F55\u505A\u4FEE\u6539\u6216\u52A0\u6392\u4ED6\u9501\u7684\u64CD\u4F5C\uFF0C\u90FD\u4F1A\u7B49\u5F85\u6211\u4EEC\u89E3\u9501\u6216\u76F4\u63A5\u629B\u51FA\u5F02\u5E38\u3002</li></ol><p><strong>\u62FF\u6BD4\u8F83\u5E38\u7528\u7684MySql Innodb\u5F15\u64CE\u4E3E\u4F8B\uFF0C\u6765\u8BF4\u660E\u4E00\u4E0B\u5728SQL\u4E2D\u5982\u4F55\u4F7F\u7528\u60B2\u89C2\u9501\u3002</strong></p><p>\u8981\u4F7F\u7528\u60B2\u89C2\u9501\uFF0C\u6211\u4EEC\u5FC5\u987B\u5173\u95EDMySQL\u6570\u636E\u5E93\u7684\u81EA\u52A8\u63D0\u4EA4\u5C5E\u6027\u3002\u56E0\u4E3AMySQL\u9ED8\u8BA4\u4F7F\u7528autocommit\u6A21\u5F0F\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5F53\u6211\u4EEC\u6267\u884C\u4E00\u4E2A\u66F4\u65B0\u64CD\u4F5C\u540E\uFF0CMySQL\u4F1A\u7ACB\u523B\u5C06\u7ED3\u679C\u8FDB\u884C\u63D0\u4EA4\u3002\uFF08sql\u8BED\u53E5\uFF1Aset autocommit=0\uFF09</p><p>\u4EE5\u6DD8\u5B9D\u4E0B\u5355\u8FC7\u7A0B\u4E2D\u6263\u51CF\u5E93\u5B58\u7684\u9700\u6C42\u8BF4\u660E\u4E00\u4E0B\u60B2\u89C2\u9501\u7684\u4F7F\u7528\uFF1A</p><p><img src="'+s+'" alt="" loading="lazy"></p><p>\u4EE5\u4E0A\uFF0C\u5728\u5BF9id = 1\u7684\u8BB0\u5F55\u4FEE\u6539\u524D\uFF0C\u5148\u901A\u8FC7for update\u7684\u65B9\u5F0F\u8FDB\u884C\u52A0\u9501\uFF0C\u7136\u540E\u518D\u8FDB\u884C\u4FEE\u6539\u3002\u8FD9\u5C31\u662F\u6BD4\u8F83\u5178\u578B\u7684\u60B2\u89C2\u9501\u7B56\u7565\u3002</p><p>\u5982\u679C\u4EE5\u4E0A\u4FEE\u6539\u5E93\u5B58\u7684\u4EE3\u7801\u53D1\u751F\u5E76\u53D1\uFF0C\u540C\u4E00\u65F6\u95F4\u53EA\u6709\u4E00\u4E2A\u7EBF\u7A0B\u53EF\u4EE5\u5F00\u542F\u4E8B\u52A1\u5E76\u83B7\u5F97id=1\u7684\u9501\uFF0C\u5176\u5B83\u7684\u4E8B\u52A1\u5FC5\u987B\u7B49\u672C\u6B21\u4E8B\u52A1\u63D0\u4EA4\u4E4B\u540E\u624D\u80FD\u6267\u884C\u3002\u8FD9\u6837\u6211\u4EEC\u53EF\u4EE5\u4FDD\u8BC1\u5F53\u524D\u7684\u6570\u636E\u4E0D\u4F1A\u88AB\u5176\u5B83\u4E8B\u52A1\u4FEE\u6539\u3002</p><p>\u4E0A\u9762\u6211\u4EEC\u63D0\u5230\uFF0C\u4F7F\u7528select\u2026for update\u4F1A\u628A\u6570\u636E\u7ED9\u9501\u4F4F\uFF0C\u4E0D\u8FC7\u6211\u4EEC\u9700\u8981\u6CE8\u610F\u4E00\u4E9B\u9501\u7684\u7EA7\u522B\uFF0CMySQL InnoDB\u9ED8\u8BA4\u884C\u7EA7\u9501\u3002\u884C\u7EA7\u9501\u90FD\u662F\u57FA\u4E8E\u7D22\u5F15\u7684\uFF0C\u5982\u679C\u4E00\u6761SQL\u8BED\u53E5\u7528\u4E0D\u5230\u7D22\u5F15\u662F\u4E0D\u4F1A\u4F7F\u7528\u884C\u7EA7\u9501\u7684\uFF0C\u4F1A\u4F7F\u7528\u8868\u7EA7\u9501\u628A\u6574\u5F20\u8868\u9501\u4F4F\uFF0C\u8FD9\u70B9\u9700\u8981\u6CE8\u610F\u3002</p><h3 id="\u4E50\u89C2\u9501\u5B9E\u73B0\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u4E50\u89C2\u9501\u5B9E\u73B0\u65B9\u5F0F" aria-hidden="true">#</a> \u4E50\u89C2\u9501\u5B9E\u73B0\u65B9\u5F0F</h3><p>\u4F7F\u7528\u4E50\u89C2\u9501\u5C31\u4E0D\u9700\u8981\u501F\u52A9\u6570\u636E\u5E93\u7684\u9501\u673A\u5236\u4E86\u3002</p><p>\u4E50\u89C2\u9501\u7684\u6982\u5FF5\u4E2D\u5176\u5B9E\u5DF2\u7ECF\u9610\u8FF0\u4E86\u5B83\u7684\u5177\u4F53\u5B9E\u73B0\u7EC6\u8282\u3002\u4E3B\u8981\u5C31\u662F\u4E24\u4E2A\u6B65\u9AA4\uFF1A\u51B2\u7A81\u68C0\u6D4B\u548C\u6570\u636E\u66F4\u65B0\u3002\u5176\u5B9E\u73B0\u65B9\u5F0F\u6709\u4E00\u79CD\u6BD4\u8F83\u5178\u578B\u7684\u5C31\u662FCAS(Compare and Swap)\u3002</p><p>CAS\u662F\u9879\u4E50\u89C2\u9501\u6280\u672F\uFF0C\u5F53\u591A\u4E2A\u7EBF\u7A0B\u5C1D\u8BD5\u4F7F\u7528CAS\u540C\u65F6\u66F4\u65B0\u540C\u4E00\u4E2A\u53D8\u91CF\u65F6\uFF0C\u53EA\u6709\u5176\u4E2D\u4E00\u4E2A\u7EBF\u7A0B\u80FD\u66F4\u65B0\u53D8\u91CF\u7684\u503C\uFF0C\u800C\u5176\u5B83\u7EBF\u7A0B\u90FD\u5931\u8D25\uFF0C\u5931\u8D25\u7684\u7EBF\u7A0B\u5E76\u4E0D\u4F1A\u88AB\u6302\u8D77\uFF0C\u800C\u662F\u88AB\u544A\u77E5\u8FD9\u6B21\u7ADE\u4E89\u4E2D\u5931\u8D25\uFF0C\u5E76\u53EF\u4EE5\u518D\u6B21\u5C1D\u8BD5\u3002</p><p>\u6BD4\u5982\u524D\u9762\u7684\u6263\u51CF\u5E93\u5B58\u95EE\u9898\uFF0C\u901A\u8FC7\u4E50\u89C2\u9501\u53EF\u4EE5\u5B9E\u73B0\u5982\u4E0B\uFF1A</p><p><img src="'+e+'" alt="" loading="lazy"></p><p>\u4EE5\u4E0A\uFF0C\u6211\u4EEC\u5728\u66F4\u65B0\u4E4B\u524D\uFF0C\u5148\u67E5\u8BE2\u4E00\u4E0B\u5E93\u5B58\u8868\u4E2D\u5F53\u524D\u5E93\u5B58\u6570\uFF08quantity\uFF09\uFF0C\u7136\u540E\u5728\u505Aupdate\u7684\u65F6\u5019\uFF0C\u4EE5\u5E93\u5B58\u6570\u4F5C\u4E3A\u4E00\u4E2A\u4FEE\u6539\u6761\u4EF6\u3002\u5F53\u6211\u4EEC\u63D0\u4EA4\u66F4\u65B0\u7684\u65F6\u5019\uFF0C\u5224\u65AD\u6570\u636E\u5E93\u8868\u5BF9\u5E94\u8BB0\u5F55\u7684\u5F53\u524D\u5E93\u5B58\u6570\u4E0E\u7B2C\u4E00\u6B21\u53D6\u51FA\u6765\u7684\u5E93\u5B58\u6570\u8FDB\u884C\u6BD4\u5BF9\uFF0C\u5982\u679C\u6570\u636E\u5E93\u8868\u5F53\u524D\u5E93\u5B58\u6570\u4E0E\u7B2C\u4E00\u6B21\u53D6\u51FA\u6765\u7684\u5E93\u5B58\u6570\u76F8\u7B49\uFF0C\u5219\u4E88\u4EE5\u66F4\u65B0\uFF0C\u5426\u5219\u8BA4\u4E3A\u662F\u8FC7\u671F\u6570\u636E\u3002</p><p>\u4EE5\u4E0A\u66F4\u65B0\u8BED\u53E5\u5B58\u5728\u4E00\u4E2A\u6BD4\u8F83\u91CD\u8981\u7684\u95EE\u9898\uFF0C\u5373\u4F20\u8BF4\u4E2D\u7684<strong>ABA\u95EE\u9898</strong>\u3002</p><p>\u6BD4\u5982\u8BF4\u4E00\u4E2A\u7EBF\u7A0Bone\u4ECE\u6570\u636E\u5E93\u4E2D\u53D6\u51FA\u5E93\u5B58\u65703\uFF0C\u8FD9\u65F6\u5019\u53E6\u4E00\u4E2A\u7EBF\u7A0Btwo\u4E5F\u4ECE\u6570\u636E\u5E93\u4E2D\u53D6\u51FA\u5E93\u5B58\u65703\uFF0C\u5E76\u4E14two\u8FDB\u884C\u4E86\u4E00\u4E9B\u64CD\u4F5C\u53D8\u6210\u4E862\uFF0C\u7136\u540Etwo\u53C8\u5C06\u5E93\u5B58\u6570\u53D8\u62103\uFF0C\u8FD9\u65F6\u5019\u7EBF\u7A0Bone\u8FDB\u884CCAS\u64CD\u4F5C\u53D1\u73B0\u6570\u636E\u5E93\u4E2D\u4ECD\u7136\u662F3\uFF0C\u7136\u540Eone\u64CD\u4F5C\u6210\u529F\u3002\u5C3D\u7BA1\u7EBF\u7A0Bone\u7684CAS\u64CD\u4F5C\u6210\u529F\uFF0C\u4F46\u662F\u4E0D\u4EE3\u8868\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u662F\u6CA1\u6709\u95EE\u9898\u7684\u3002</p><p><img src="'+n+'" alt="" loading="lazy"></p><p>\u6709\u4E00\u4E2A\u6BD4\u8F83\u597D\u7684\u529E\u6CD5\u53EF\u4EE5\u89E3\u51B3ABA\u95EE\u9898\uFF0C\u90A3\u5C31\u662F\u901A\u8FC7\u4E00\u4E2A\u5355\u72EC\u7684\u53EF\u4EE5\u987A\u5E8F\u9012\u589E\u7684version\u5B57\u6BB5\u3002\u6539\u4E3A\u4EE5\u4E0B\u65B9\u5F0F\u5373\u53EF\uFF1A</p><p><img src="'+o+'" alt="" loading="lazy"></p><p>\u4E50\u89C2\u9501\u6BCF\u6B21\u5728\u6267\u884C\u6570\u636E\u7684\u4FEE\u6539\u64CD\u4F5C\u65F6\uFF0C\u90FD\u4F1A\u5E26\u4E0A\u4E00\u4E2A\u7248\u672C\u53F7\uFF0C\u4E00\u65E6\u7248\u672C\u53F7\u548C\u6570\u636E\u7684\u7248\u672C\u53F7\u4E00\u81F4\u5C31\u53EF\u4EE5\u6267\u884C\u4FEE\u6539\u64CD\u4F5C\u5E76\u5BF9\u7248\u672C\u53F7\u6267\u884C+1\u64CD\u4F5C\uFF0C\u5426\u5219\u5C31\u6267\u884C\u5931\u8D25\u3002\u56E0\u4E3A\u6BCF\u6B21\u64CD\u4F5C\u7684\u7248\u672C\u53F7\u90FD\u4F1A\u968F\u4E4B\u589E\u52A0\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u51FA\u73B0ABA\u95EE\u9898\uFF0C\u56E0\u4E3A\u7248\u672C\u53F7\u53EA\u4F1A\u589E\u52A0\u4E0D\u4F1A\u51CF\u5C11\u3002</p><p><img src="'+l+'" alt="" loading="lazy"></p><p>\u9664\u4E86version\u4EE5\u5916\uFF0C\u8FD8\u53EF\u4EE5\u4F7F\u7528\u65F6\u95F4\u6233\uFF0C\u56E0\u4E3A\u65F6\u95F4\u6233\u5929\u7136\u5177\u6709\u987A\u5E8F\u9012\u589E\u6027\u3002<br> \u4EE5\u4E0ASQL\u5176\u5B9E\u8FD8\u662F\u6709\u4E00\u5B9A\u7684\u95EE\u9898\u7684\uFF0C\u5C31\u662F\u4E00\u65E6\u9047\u4E0A\u9AD8\u5E76\u53D1\u7684\u65F6\u5019\uFF0C\u5C31\u53EA\u6709\u4E00\u4E2A\u7EBF\u7A0B\u53EF\u4EE5\u4FEE\u6539\u6210\u529F\uFF0C\u90A3\u4E48\u5C31\u4F1A\u5B58\u5728\u5927\u91CF\u7684\u5931\u8D25\u3002</p><p>\u5BF9\u4E8E\u50CF\u6DD8\u5B9D\u8FD9\u6837\u7684\u7535\u5546\u7F51\u7AD9\uFF0C\u9AD8\u5E76\u53D1\u662F\u5E38\u6709\u7684\u4E8B\uFF0C\u603B\u8BA9\u7528\u6237\u611F\u77E5\u5230\u5931\u8D25\u663E\u7136\u662F\u4E0D\u5408\u7406\u7684\u3002\u6240\u4EE5\uFF0C\u8FD8\u662F\u8981\u60F3\u529E\u6CD5\u51CF\u5C11\u4E50\u89C2\u9501\u7684\u7C92\u5EA6\u7684\u3002</p><p>\u6709\u4E00\u6761\u6BD4\u8F83\u597D\u7684\u5EFA\u8BAE\uFF0C\u53EF\u4EE5\u51CF\u5C0F\u4E50\u89C2\u9501\u529B\u5EA6\uFF0C\u6700\u5927\u7A0B\u5EA6\u7684\u63D0\u5347\u541E\u5410\u7387\uFF0C\u63D0\u9AD8\u5E76\u53D1\u80FD\u529B\uFF01\u5982\u4E0B\uFF1A</p><p><img src="'+d+'" alt="" loading="lazy"></p><p>\u4EE5\u4E0ASQL\u8BED\u53E5\u4E2D\uFF0C\u5982\u679C\u7528\u6237\u4E0B\u5355\u6570\u4E3A1\uFF0C\u5219\u901A\u8FC7quantity - 1 &gt; 0\u7684\u65B9\u5F0F\u8FDB\u884C\u4E50\u89C2\u9501\u63A7\u5236\u3002</p><p>\u4EE5\u4E0Aupdate\u8BED\u53E5\uFF0C\u5728\u6267\u884C\u8FC7\u7A0B\u4E2D\uFF0C\u4F1A\u5728\u4E00\u6B21\u539F\u5B50\u64CD\u4F5C\u4E2D\u81EA\u5DF1\u67E5\u8BE2\u4E00\u904Dquantity\u7684\u503C\uFF0C\u5E76\u5C06\u5176\u6263\u51CF\u63891\u3002</p><p>\u9AD8\u5E76\u53D1\u73AF\u5883\u4E0B\u9501\u7C92\u5EA6\u628A\u63A7\u662F\u4E00\u95E8\u91CD\u8981\u7684\u5B66\u95EE\uFF0C\u9009\u62E9\u4E00\u4E2A\u597D\u7684\u9501\uFF0C\u5728\u4FDD\u8BC1\u6570\u636E\u5B89\u5168\u7684\u60C5\u51B5\u4E0B\uFF0C\u53EF\u4EE5\u5927\u5927\u63D0\u5347\u541E\u5410\u7387\uFF0C\u8FDB\u800C\u63D0\u5347\u6027\u80FD\u3002</p><h2 id="\u5982\u4F55\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u9009\u62E9" aria-hidden="true">#</a> \u5982\u4F55\u9009\u62E9</h2><p>\u5728\u4E50\u89C2\u9501\u4E0E\u60B2\u89C2\u9501\u7684\u9009\u62E9\u4E0A\u9762\uFF0C\u4E3B\u8981\u770B\u4E0B\u4E24\u8005\u7684\u533A\u522B\u4EE5\u53CA\u9002\u7528\u573A\u666F\u5C31\u53EF\u4EE5\u4E86\u3002s</p><ol><li><p>\u4E50\u89C2\u9501\u5E76\u672A\u771F\u6B63\u52A0\u9501\uFF0C\u6548\u7387\u9AD8\u3002\u4E00\u65E6\u9501\u7684\u7C92\u5EA6\u638C\u63E1\u4E0D\u597D\uFF0C\u66F4\u65B0\u5931\u8D25\u7684\u6982\u7387\u5C31\u4F1A\u6BD4\u8F83\u9AD8\uFF0C\u5BB9\u6613\u53D1\u751F\u4E1A\u52A1\u5931\u8D25\u3002</p></li><li><p>\u60B2\u89C2\u9501\u4F9D\u8D56\u6570\u636E\u5E93\u9501\uFF0C\u6548\u7387\u4F4E\u3002\u66F4\u65B0\u5931\u8D25\u7684\u6982\u7387\u6BD4\u8F83\u4F4E\u3002</p></li></ol><p>\u968F\u7740\u4E92\u8054\u7F51\u4E09\u9AD8\u67B6\u6784\uFF08\u9AD8\u5E76\u53D1\u3001\u9AD8\u6027\u80FD\u3001\u9AD8\u53EF\u7528\uFF09\u7684\u63D0\u51FA\uFF0C\u60B2\u89C2\u9501\u5DF2\u7ECF\u8D8A\u6765\u8D8A\u5C11\u7684\u88AB\u4F7F\u7528\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\u4E86\uFF0C\u5C24\u5176\u662F\u5E76\u53D1\u91CF\u6BD4\u8F83\u5927\u7684\u4E1A\u52A1\u573A\u666F\u3002</p>',60);function g(_,m){return h}var S=a(c,[["render",g],["__file","\u4EC0\u4E48\u662F\u4E50\u89C2\u9501,\u4EC0\u4E48\u662F\u60B2\u89C2\u9501.html.vue"]]);export{S as default};
